  import { Routes, Route} from "react-router-dom";
  import { useState } from "react";
  import UserList from "../components/Crud User/UserList";
  import AddUser from "../components/Crud User/AddUser";
  import EditUser from "../components/Crud User/EditUser";
  import Login from "../components/Page/Home/Login";
  import Register from "../components/Page/Home/ForgetPass";
  import Home from '../components/Page/Home/Home'
  import Headers from "../components/Page/Dashboard/Headers";
  import Sidebars from "../components/Page/Dashboard/Sidebars";
  import Mains from "../components/Page/Dashboard/Mains";
  


  function AppRoutes() {
    const [darkMode, setDarkMode] = useState(false);
    const [isSidebarOpen, setIsSidebarOpen] = useState(false);

    const toggleDarkMode = () => {
      setDarkMode(!darkMode);
    };
    const toggleSidebar = () => {
      setIsSidebarOpen(!isSidebarOpen);
    };

    return (
      <div className={`${darkMode && "dark"}`}>
        <Headers toggleDarkMode={toggleDarkMode} darkMode={darkMode}  toggleSidebar={toggleSidebar}/>
        <Sidebars isSidebarOpen={isSidebarOpen}/>
        <Mains>

        </Mains>
      <Routes>
        <Route path="/" element={<Home/>}/>
        <Route path="/Headers" element={<Headers />}/>
        <Route path="/Login" element={<Login/>}/>
        <Route path="/register" element={<Register/>}/>
        <Route path="/users" element={<UserList/>}/>
        <Route path="add" element={<AddUser/>}/>
        <Route path="edit/:id" element={<EditUser/>}/>
      </Routes>
      </div>
    );
  }

  export default AppRoutes;
