import { useLocation } from "react-router-dom";
import clsx from "clsx";
import { Moon, Sun, User } from 'lucide-react';
import Logo2 from "../../../assets/Logo2.png";

const Headers = ({ darkMode, toggleDarkMode }) => {
    const location = useLocation();
    const excludedPaths = ["/", "/login"];
    const isExcludedPath = excludedPaths.includes(location.pathname);

    return (
        <nav className={clsx(
            "fixed left-0 top-0 z-50 h-full w-16 bg-white flex flex-col items-center space-y-4 py-6",
            "border-r border-gray-200 dark:bg-gray-800 dark:border-gray-700"
        )}>
            {/* Logo */}
            <a href="/#" className="flex items-center flex-col">
                <img src={Logo2} alt="Logo" width="40" height="40" />
                <span className="self-center text-xs font-semibold sm:text-sm whitespace-nowrap dark:text-white mt-2">SIOPS</span>
            </a>

            {/* Dark Mode Toggle */}
            <button
                className="dark:bg-slate-50 dark:text-slate-700 p-2 rounded-full"
                onClick={toggleDarkMode}
            >
                {darkMode ? <Sun /> : <Moon />}
            </button>

            {/* User Button */}
            {isExcludedPath && (
                <a href="/" className="text-lg font-bold text-black dark:text-gray-300">
                    <button className="flex items-center justify-center dark:bg-white w-10 h-10 rounded-full shadow-md">
                        <User size={24} className="dark:text-black" />
                    </button>
                </a>
            )}
        </nav>
    );
};

export default Headers;
