import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { 
  Package, 
  Users, 
  ShoppingCart, 
  TrendingUp, 
  Clipboard,
  FilePlus,
  Edit,
  Trash2,
  X
} from 'lucide-react';
import CrudButton from '../../../Button/CrudButton';
import SuccessModal from '../../../modal/SuccessModal';
import AlertModal from '../../../modal/AlertModal';

const DashboardAdmin = () => {
  const [orders, setOrders] = useState([]);
  const [users, setUsers] = useState([]);
  const [userRole, setUserRole] = useState('');
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedUser, setSelectedUser] = useState(null);
  const [modalMode, setModalMode] = useState('add');
  const [successModalOpen, setSuccessModalOpen] = useState(false);
  const [errorModalOpen, setErrorModalOpen] = useState(false);
  const [modalMessage, setModalMessage] = useState('');
  const [userForm, setUserForm] = useState({
    name: '',
    email: '',
    password: '',
    confPassword: '',
    role: 'staff'
  });

  useEffect(() => {
    fetchOrderData();
    fetchUserData();
    getRole();
  }, []);

  const getRole = () => {
    const token = localStorage.getItem('token');
    if (token) {
      const userData = JSON.parse(atob(token.split('.')[1]));
      setUserRole(userData.role);
    }
  };

  const fetchOrderData = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:5000/orders', {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
      const sortedOrders = response.data.sort((a, b) => new Date(b.date) - new Date(a.date));
      setOrders(sortedOrders);  
    } catch (error) {
      console.error('Error fetching order data:', error.response ? error.response.data : error.message);
      setOrders([]);
    }
  };

  const fetchUserData = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        throw new Error('No token found');
      }

      const response = await axios.get('http://localhost:5000/users', {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });

      setUsers(response.data);
    } catch (error) {
      console.error('Error fetching user data:', error.response ? error.response.data : error.message);
      setUsers([]);
    }
  };

  const handleAddUser = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      await axios.post('http://localhost:5000/users', userForm, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
      setIsModalOpen(false);
      fetchUserData();
      setModalMessage('User berhasil ditambahkan');
      setSuccessModalOpen(true);
      resetForm();
    } catch (error) {
      setModalMessage(error.response?.data?.msg || 'Gagal menambahkan user');
      setErrorModalOpen(true);
    }
  };

  const handleEditUser = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const dataToUpdate = { ...userForm };
      if (!dataToUpdate.password) {
        delete dataToUpdate.password;
        delete dataToUpdate.confPassword;
      }
      await axios.put(`http://localhost:5000/users/${selectedUser.id}`, dataToUpdate, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
      setIsModalOpen(false);
      fetchUserData();
      setModalMessage('User berhasil diperbarui');
      setSuccessModalOpen(true);
      resetForm();
    } catch (error) {
      setModalMessage(error.response?.data?.msg || 'Gagal memperbarui user');
      setErrorModalOpen(true);
    }
  };

  const handleDeleteUser = async (id) => {
    try {
      const token = localStorage.getItem('token');
      await axios.delete(`http://localhost:5000/users/${id}`, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
      fetchUserData();
      setModalMessage('User berhasil dihapus');
      setSuccessModalOpen(true);
    } catch (error) {
      setModalMessage(error.response?.data?.msg || 'Gagal menghapus user');
      setErrorModalOpen(true);
    }
  };

  const startEditing = (user) => {
    setSelectedUser(user);
    setUserForm({
      name: user.name,
      email: user.email,
      role: user.role,
      password: '',
      confPassword: ''
    });
    setModalMode('edit');
    setIsModalOpen(true);
  };

  const resetForm = () => {
    setUserForm({
      name: '',
      email: '',
      password: '',
      confPassword: '',
      role: 'staff'
    });
    setSelectedUser(null);
    setModalMode('add');
  };

  const calculateTotalStock = () => {
    return orders.reduce((total, order) => total + (order.quantity || 0), 0);
  };

  return (
    <div className="container mx-auto p-5 mt-16 bg-gray-50">
      <h2 className="text-3xl font-bold mb-6 text-gray-800">Stock Management Dashboard</h2>

      {/* Summary Cards */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
        <div className="bg-white p-6 rounded-xl shadow-md hover:shadow-lg transition-all">
          <div className="flex items-center justify-between">
            <div>
              <h3 className="text-lg font-semibold text-gray-600">Total Orders</h3>
              <p className="text-3xl font-bold text-blue-600">{orders.length}</p>
            </div>
            <ShoppingCart className="text-blue-500" size={40} />
          </div>
        </div>

        <div className="bg-white p-6 rounded-xl shadow-md hover:shadow-lg transition-all">
          <div className="flex items-center justify-between">
            <div>
              <h3 className="text-lg font-semibold text-gray-600">Total Stock</h3>
              <p className="text-3xl font-bold text-green-600">{calculateTotalStock()}</p>
            </div>
            <Package className="text-green-500" size={40} />
          </div>
        </div>

        <div className="bg-white p-6 rounded-xl shadow-md hover:shadow-lg transition-all">
          <div className="flex items-center justify-between">
            <div>
              <h3 className="text-lg font-semibold text-gray-600">Total Users</h3>
              <p className="text-3xl font-bold text-purple-600">{users.length}</p>
            </div>
            <Clipboard className="text-purple-500" size={40} />
          </div>
        </div>
      </div>

      {/* Recent Orders and User Management */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Recent Orders Table */}
        <div className="bg-white p-6 rounded-xl shadow-md">
          <div className="flex justify-between items-center mb-4">
            <h3 className="text-xl font-semibold text-gray-800">Recent Orders</h3>
            <TrendingUp className="text-blue-500" size={24} />
          </div>
          <div className="overflow-x-auto">
            <table className="w-full text-sm text-left">
              <thead className="bg-gray-100 text-gray-600">
                <tr>
                  <th className="p-3">Nama Barang</th>
                  <th className="p-3">Quantity</th>
                  <th className="p-3">Tanggal</th>
                </tr>
              </thead>
              <tbody>
                {orders.slice(0, 5).map(order => (
                  <tr key={order.id} className="border-b hover:bg-gray-50">
                    <td className="p-3">{order.namaBarang}</td>
                    <td className="p-3">{order.quantity}</td>
                    <td className="p-3">{new Date(order.date).toLocaleDateString()}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>

        {/* User Management */}
        <div className="bg-white p-6 rounded-xl shadow-md">
          <div className="flex justify-between items-center mb-4">
            <h3 className="text-xl font-semibold text-gray-800">User Management</h3>
            <div className="flex items-center space-x-2">
              <CrudButton
                label="Add User"
                icon={FilePlus}
                onClick={() => {
                  resetForm();
                  setModalMode('add');
                  setIsModalOpen(true);
                }}
                actionType="add"
                buttonStyle="primary"
              />
              <Users className="text-green-500" size={24} />
            </div>
          </div>
          <div className="overflow-x-auto">
            <table className="w-full text-sm text-left">
              <thead className="bg-gray-100 text-gray-600">
                <tr>
                  <th className="p-3">Name</th>
                  <th className="p-3">Email</th>
                  <th className="p-3">Role</th>
                  <th className="p-3">Actions</th>
                </tr>
              </thead>
              <tbody>
                {users.map(user => (
                  <tr 
                    key={user.id} 
                    className="border-b border-gray-200 hover:bg-gray-100 transition"
                  >
                    <td className="py-3 px-4">{user.name}</td>
                    <td className="py-3 px-4">{user.email}</td>
                    <td className="py-3 px-4">
                      <span className={`px-3 py-1 rounded-full text-xs ${
                        user.role === 'admin' ? 'bg-red-200 text-red-800' : 
                        user.role === 'staff' ? 'bg-green-200 text-green-800' : 
                        'bg-blue-200 text-blue-800'
                      }`}>
                        {user.role}
                      </span>
                    </td>
                    <td className="py-3 px-4">
                      <div className="flex space-x-2">
                        <CrudButton
                          label="Edit"
                          icon={Edit}
                          onClick={() => startEditing(user)}
                          actionType="edit"
                          buttonStyle="secondary"
                        />
                        <CrudButton
                          label="Delete"
                          icon={Trash2}
                          onClick={() => handleDeleteUser(user.id)}
                          actionType="delete"
                          buttonStyle="danger"
                        />
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>

      {/* User Modal */}
      {isModalOpen && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 w-full max-w-md">
            <div className="flex justify-between items-center mb-4">
              <h3 className="text-xl font-semibold">
                {modalMode === 'edit' ? 'Edit User' : 'Add New User'}
              </h3>
              <button
                onClick={() => {
                  setIsModalOpen(false);
                  resetForm();
                }}
                className="text-gray-500 hover:text-gray-700"
              >
                <X size={24} />
              </button>
            </div>
            <form onSubmit={modalMode === 'edit' ? handleEditUser : handleAddUser}>
              <div className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700">Name</label>
                  <input
                    type="text"
                    value={userForm.name}
                    onChange={(e) => setUserForm({ ...userForm, name: e.target.value })}
                    className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                    required
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700">Email</label>
                  <input
                    type="email"
                    value={userForm.email}
                    onChange={(e) => setUserForm({ ...userForm, email: e.target.value })}
                    className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                    required
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700">Password</label>
                  <input
                    type="password"
                    value={userForm.password}
                    onChange={(e) => setUserForm({ ...userForm, password: e.target.value })}
                    className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                    required={modalMode === 'add'}
                    placeholder={modalMode === 'edit' ? "Leave blank to keep current password" : ""}
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700">Confirm Password</label>
                  <input
                    type="password"
                    value={userForm.confPassword}
                    onChange={(e) => setUserForm({ ...userForm, confPassword: e.target.value })}
                    className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                    required={modalMode === 'add'}
                    placeholder={modalMode === 'edit' ? "Leave blank to keep current password" : ""}
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700">Role</label>
                  <select
                    value={userForm.role}
                    onChange={(e) => setUserForm({ ...userForm, role: e.target.value })}
                    className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                  >
                    <option value="user">User</option>
                    <option value="staff">Staff</option>
                    <option value="admin">Admin</option>
                  </select>
                </div>
              </div>
              <div className="mt-6 flex justify-end space-x-3">
                <button
                  type="button"
                  onClick={() => {
                    setIsModalOpen(false);
                    resetForm();
                  }}
                  className="px-4 py-2 text-sm font-medium text-gray-700 bg-gray-100 rounded-md hover:bg-gray-200"
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  className="px-4 py-2 text-sm font-medium text-white bg-blue-600 rounded-md hover:bg-blue-700"
                >
                  {modalMode === 'edit' ? 'Update' : 'Add'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Success Modal */}
      <SuccessModal
        isOpen={successModalOpen}
        onClose={() => setSuccessModalOpen(false)}
        message={modalMessage}
      />

      {/* Error Modal */}
      <AlertModal
        isOpen={errorModalOpen}
        onClose={() => setErrorModalOpen(false)}
        message={modalMessage}
      />
    </div>
  );
};

export default DashboardAdmin;
