import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {Link} from "react-router-dom"
import { FilePlus } from 'lucide-react';

const UserList = () => {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    getUsers();
  }, []);

  const getUsers = async () => {
    try {
      const response = await axios.get('http://localhost:5000/users');
      setUsers(response.data);
    } catch (error) {
      console.error('Error fetching users:', error);
    }
  };

const deleteUser = async (id) =>{
    try {
        await axios.delete(`http://localhost:5000/users/${id}`);
        getUsers();
    } catch (error) {
        console.log(error);
    }
}


  return (
    <div className="flex justify-center mt-5">
      <div className="w-3/4">

        <table className="table-auto w-full border-collapse border border-gray-200 mt-5">
          <thead>
            <tr className="bg-gray-200">
              <th className="border px-4 py-2">No</th>
              <th className="border px-4 py-2">Name</th>
              <th className="border px-4 py-2">Email</th>
              <th className="border px-4 py-2">Password</th>
              <th className="border px-4 py-2">Gender</th>
              <Link 
             to="/add"
            className="bg-green-500 text-white font-bold py-2 px-4 rounded flex items-center" // Penambahan class space-x-2
             >
            <FilePlus className="w-4 h-4" /> 
            Tambah
             </Link>
              
            </tr>
          </thead>
          <tbody>
            {users.map((user, index) => (
              <tr key={user.id}>
                <td className="border px-4 py-2">{index + 1}</td>
                <td className="border px-4 py-2">{user.name}</td>
                <td className="border px-4 py-2">{user.email}</td>
                <td className="border px-4 py-2">{user.password}</td>
                <td className="border px-4 py-2">{user.gender}</td>
                <td className="border pl-4 py-2">
                  <Link to={`/edit/${user.id}`} className="bg-blue-500 text-white px-3 py-1 rounded mr-2 text-xs">Edit</Link>
                  <button onClick={()=> deleteUser(user.id)} className="bg-red-500 text-white px-3 py-1 rounded text-xs">Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default UserList;
