import React, { useState } from "react";
import clsx from "clsx";
import { Outlet } from "react-router-dom";
import SidebarAdmin from "../Sidebar/admin/SidebarAdmin";
import Sidebars from "../Sidebar/staff/Sidebars";
import Headers from "../Sidebar/Headers";

const DashboardLayout = ({ toggleDarkMode, darkMode }) => {
    const [isSidebarOpen, setIsSidebarOpen] = useState(false);
    const role = localStorage.getItem("role");
    const SidebarComponent = role === "admin" ? SidebarAdmin : Sidebars;

    // Fungsi toggle untuk membuka/menutup sidebar
    const toggleSidebar = () => {
        setIsSidebarOpen((prev) => !prev);
    };

    return (
        <div className="flex min-h-screen">
            {/* Sidebar */}
            <div
                className={clsx(
                    "fixed z-40 inset-y-0 left-0 bg-white dark:bg-gray-800 shadow-lg",
                    "transition-transform duration-300 ease-in-out",
                    isSidebarOpen ? "translate-x-0" : "-translate-x-full", // Mobile
                    "lg:translate-x-0 lg:w-64" // Selalu terlihat di layar besar
                )}
            >
                <SidebarComponent
                    isSidebarOpen={isSidebarOpen}
                    darkMode={darkMode}
                    toggleDarkMode={toggleDarkMode}
                />
            </div>

            {/* Main Content */}
            <div
                className={clsx(
                    "flex-1 transition-all duration-300 ease-in-out",
                    isSidebarOpen ? "lg:ml-64" : "lg:ml-0" // Pergeseran konten utama
                )}
            >
                {/* Header - hanya dirender sekali di sini */}
                <Headers
                    toggleDarkMode={toggleDarkMode}
                    darkMode={darkMode}
                    toggleSidebar={toggleSidebar}
                />

                {/* Konten Utama */}
                <main className="p-4 bg-gray-100">
                    <Outlet />
                </main>
            </div>
        </div>
    );
};

export default DashboardLayout;
