import React, { useState, useEffect } from 'react';

const OrderStock = () => {
  const [stocks, setStocks] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [newStock, setNewStock] = useState({
    id: '',
    noPembelian: '',
    kodeBarang: '',
    barcode: '',
    namaBarang: '',
    hargaBeli: '',
    image: null,
  });
  const [isEditing, setIsEditing] = useState(false);
  const [editId, setEditId] = useState(null);

  // Mengambil tanggal hari ini otomatis
  const getCurrentDate = () => {
    const today = new Date();
    return today.toISOString().split('T')[0]; // Mengambil format YYYY-MM-DD
  };

  // Handle perubahan input form
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewStock({ ...newStock, [name]: value });
  };

  // Handle upload gambar
  const handleImageUpload = (e) => {
    setNewStock({ ...newStock, image: URL.createObjectURL(e.target.files[0]) });
  };

  // Menambahkan order stok baru
  const handleAddStock = (e) => {
    e.preventDefault();
    if (isEditing) {
      setStocks(stocks.map(stock => stock.id === editId ? { ...newStock, id: editId } : stock));
      setIsEditing(false);
    } else {
      setStocks([...stocks, { ...newStock, id: Date.now(), tanggal: getCurrentDate() }]);
    }
    setNewStock({
      id: '',
      noPembelian: '',
      kodeBarang: '',
      barcode: '',
      namaBarang: '',
      hargaBeli: '',
      image: null,
    });
  };

  // Mengedit data order stok
  const handleEditStock = (id) => {
    const stockToEdit = stocks.find(stock => stock.id === id);
    setNewStock(stockToEdit);
    setEditId(id);
    setIsEditing(true);
  };

  // Menghapus data order stok
  const handleDeleteStock = (id) => {
    if (window.confirm('Apakah Anda yakin ingin menghapus data ini?')) {
      setStocks(stocks.filter(stock => stock.id !== id));
    }
  };

  // Filter untuk pencarian
  const filteredStocks = stocks.filter((stock) => {
    return (
      stock.kodeBarang.toLowerCase().includes(searchTerm.toLowerCase()) ||
      stock.namaBarang.toLowerCase().includes(searchTerm.toLowerCase())
    );
  });

  return (
    <div className="container mx-auto p-5 mt-12 mr-10">
      <h1 className="text-3xl font-bold mb-4">Order Stok</h1>

      {/* Search Input */}
      <input
        type="text"
        className="border p-2 mb-4 w-full"
        placeholder="Cari berdasarkan Kode Barang atau Nama Barang"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />

      {/* Form Tambah/Edit */}
      <form onSubmit={handleAddStock} className="mb-5">
        <div className="grid grid-cols-2 gap-4">
          <input
            type="text"
            name="noPembelian"
            placeholder="No Pembelian"
            value={newStock.noPembelian}
            onChange={handleInputChange}
            required
            className="border p-2"
          />
          <input
            type="text"
            name="kodeBarang"
            placeholder="Kode Barang"
            value={newStock.kodeBarang}
            onChange={handleInputChange}
            required
            className="border p-2"
          />
          <input
            type="text"
            name="barcode"
            placeholder="Barcode"
            value={newStock.barcode}
            onChange={handleInputChange}
            required
            className="border p-2"
          />
          <input
            type="text"
            name="namaBarang"
            placeholder="Nama Barang"
            value={newStock.namaBarang}
            onChange={handleInputChange}
            required
            className="border p-2"
          />
          <input
            type="number"
            name="hargaBeli"
            placeholder="Harga Beli"
            value={newStock.hargaBeli}
            onChange={handleInputChange}
            required
            className="border p-2"
          />
          <input type="file" onChange={handleImageUpload} className="border p-2" />
        </div>
        <button
          type="submit"
          className="bg-blue-500 text-white px-4 py-2 mt-4"
        >
          {isEditing ? 'Update' : 'Tambah'} Stok
        </button>
      </form>

      {/* Tabel Data */}
      <table className="min-w-full table-auto bg-white shadow-md rounded-lg">
        <thead>
          <tr className="bg-gray-200">
            <th className="p-2">No Pembelian</th>
            <th className="p-2">Tanggal</th>
            <th className="p-2">Image</th>
            <th className="p-2">Kode Barang</th>
            <th className="p-2">Barcode</th>
            <th className="p-2">Nama Barang</th>
            <th className="p-2">Harga Beli</th>
            <th className="p-2">Actions</th>
          </tr>
        </thead>
        <tbody>
          {filteredStocks.length > 0 ? (
            filteredStocks.map((stock) => (
              <tr key={stock.id} className="border-t">
                <td className="p-2">{stock.noPembelian}</td>
                <td className="p-2">{stock.tanggal}</td>
                <td className="p-2">
                  {stock.image ? (
                    <img src={stock.image} alt={stock.namaBarang} className="w-16 h-16 object-cover" />
                  ) : (
                    'N/A'
                  )}
                </td>
                <td className="p-2">{stock.kodeBarang}</td>
                <td className="p-2">{stock.barcode}</td>
                <td className="p-2">{stock.namaBarang}</td>
                <td className="p-2">Rp {stock.hargaBeli}</td>
                <td className="p-2">
                  <button
                    onClick={() => handleEditStock(stock.id)}
                    className="bg-yellow-400 text-white px-3 py-1 rounded mr-2"
                  >
                    Edit
                  </button>
                  <button
                    onClick={() => handleDeleteStock(stock.id)}
                    className="bg-red-500 text-white px-3 py-1 rounded"
                  >
                    Hapus
                  </button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="8" className="text-center p-4">
                Data tidak ditemukan.
              </td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default OrderStock;
