import React from "react";
import SidebarAdmin from "../Sidebar/admin/SidebarAdmin";
import Sidebars from "../Sidebar/staff/Sidebars";
import Headers from "../Sidebar/Headers";

const DashboardLayout = ({ children, toggleDarkMode, darkMode, toggleSidebar }) => {
    const role = localStorage.getItem("role");
    const SidebarComponent = role === "admin" ? SidebarAdmin : Sidebars;

    return (
        <div className="flex min-h-screen bg-gray-100">
            {/* Vertical Header */}
            <Headers darkMode={darkMode} toggleDarkMode={toggleDarkMode} />

            {/* Sidebar */}
            <SidebarComponent />

            {/* Main Content */}
            <div className="flex-1 p-4 ml-16"> {/* Adjust margin to accommodate header width */}
                {children}
            </div>
        </div>
    );
};

export default DashboardLayout;
