import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import CosForm from './CosForm'; // Ensure the path is correct

const ForgetPassword = () => {
  const [email, setEmail] = useState("");
  const navigate = useNavigate();

  const handleForgetPassword = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/forgot-password', {
        email,
      });
      // Navigate to a confirmation page or show a message
      navigate("/password-reset-confirmation");
    } catch (error) {
      console.error(error);
    }
  }

  return (
    <CosForm title="Forget Password" onSubmit={handleForgetPassword}>
      <div className="mb-4">
        <label className="block text-white text-sm font-bold mb-2" htmlFor="email">
          Email
        </label>
        <input
          className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
          id="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          type="email"
          placeholder="Enter your email"
        />
      </div>
      <div className="flex items-center justify-between">
        <button
          className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
          type="submit"
        >
          Send Reset Link
        </button>
      </div>
      <div className="text-center mt-4">
        <a href="/login" className="text-white hover:underline">
          Remember your password? Login
        </a>
      </div>
    </CosForm>
  );
}

export default ForgetPassword;
