import { Routes, Route } from "react-router-dom";
import { useState } from "react";
import UserList from "../components/Page/CrudUser/UserList";
import AddUser from "../components/Page/CrudUser/AddUser";
import EditUser from "../components/Page/CrudUser/EditUser";
import Login from "../components/Page/Home/Login";
import ForgetPass from "../components/Page/Home/ForgetPass";
import Home from '../components/Page/Home/Home'
import Headers from "../components/Page/Dashboard/Headers";
import Sidebars from "../components/Page/Dashboard/Sidebars";
import Mains from "../components/Page/Dashboard/Mains";

function AppRoutes() {
  const [darkMode, setDarkMode] = useState(false);
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
  };
  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  return (
    <div className={`${darkMode && "dark"}`}>
      <Routes>
        <Route path="/login" element={<Login />} />
        <Route path="/forgetpass" element={<ForgetPass />} />
        <Route path="/home" element={<Home />} />
        <Route path="/" element={
          <>
            <Headers toggleDarkMode={toggleDarkMode} darkMode={darkMode} toggleSidebar={toggleSidebar} />
            <Sidebars isSidebarOpen={isSidebarOpen} />
            <Mains>
              <Routes>
                <Route path="/" element={<Home />} />
                <Route path="/users" element={<UserList />} />
                <Route path="/users/add" element={<AddUser />} />
                <Route path="/users/edit/:id" element={<EditUser />} />
              </Routes>
            </Mains>
          </>
        }>
        </Route>
      </Routes>
    </div>
  );
}

export default AppRoutes;
