// src/components/CustomAlert.js
import React from "react";

const CustomAlert = ({ isOpen, onClose, title, text }) => {
  return (
    <>
      {isOpen && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50 transition-opacity duration-300 ease-out opacity-100">
          <div className="bg-white rounded-lg p-6 w-96 text-center transform transition-transform duration-300 ease-out scale-100">
            <h2 className="text-2xl font-semibold mb-4">{title}</h2>
            <p className="mb-6">{text}</p>
            <button
              className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
              onClick={onClose}
            >
              OK
            </button>
          </div>
        </div>
      )}
    </>
  );
};

export default CustomAlert;
