import { Routes, Route, Navigate, Outlet } from "react-router-dom";
import { useState, useEffect } from "react";

import Login from "../components/Page/Home/Login";
import OrderStock from "../components/Page/Order/OrderStock";
import DashboardAdmin from "../components/Page/Sidebar/admin/DashboardAdmin";
import DashboardStaff from "../components/Page/Sidebar/staff/DashboardStaff";
import DashboardLayout from "../components/Page/Home/DashboardLayout";
import UserProfile from "../components/Page/Profile/UserProfile";
import Product from "../components/Page/Product/Product";


function AppRoutes() {
  const [userRole, setUserRole] = useState(null);

  useEffect(() => {
    const role = getUserRole();
    setUserRole(role);
  }, []);

  const isAuthenticated = () => {
    const token = localStorage.getItem("token");
    if (!token) return false;

    try {
      const userData = JSON.parse(atob(token.split(".")[1]));
      const currentTime = Math.floor(Date.now() / 1000);
      return userData.exp > currentTime;
    } catch {
      return false;
    }
  };

  const getUserRole = () => {
    const token = localStorage.getItem("token");
    if (!token) return null;

    try {
      const userData = JSON.parse(atob(token.split(".")[1]));
      return userData.role || null;
    } catch {
      return null;
    }
  };

  const ProtectedRoute = ({ children, allowedRoles = [] }) => {
    const auth = isAuthenticated();
    if (!auth) return <Navigate to="/login" replace />;
    if (allowedRoles.length > 0 && !allowedRoles.includes(userRole)) {
      return <Navigate to={userRole === "admin" ? "/dashboardAdmin" : "/dashboard"} replace />;
    }
    return children;
  };

  const PublicRoute = ({ children }) => {
    const auth = isAuthenticated();
    if (auth) return <Navigate to={userRole === "admin" ? "/dashboardAdmin" : "/dashboard"} replace />;
    return children;
  };

  return (
    <Routes>
      {/* Public Routes */}
      <Route
        path="/login"
        element={
          <PublicRoute>
            <Login />
          </PublicRoute>
        }
      />

      {/* Protected Routes */}
      <Route
        element={
          <ProtectedRoute>
            <DashboardLayout />
          </ProtectedRoute>
        }
      >
        <Route index element={<Navigate to={userRole === "admin" ? "/dashboardAdmin" : "/dashboard"} replace />} />
        <Route path="dashboardAdmin" element={<DashboardAdmin />} />
        <Route path="dashboard" element={<DashboardStaff />} />
        <Route path="product" element={<Product />} />
        <Route path="userprofile" element={<UserProfile />} />
        <Route path="order" element={<OrderStock />} />
      </Route>

      {/* Catch-All Route */}
      <Route
        path="*"
        element={
          isAuthenticated() ? (
            <Navigate to={userRole === "admin" ? "/dashboardAdmin" : "/dashboard"} replace />
          ) : (
            <NotFoundPage />
          )
        }
      />
    </Routes>
  );
}

export default AppRoutes;
