import React, { useState } from "react";
import { useLocation } from "react-router-dom";
import clsx from "clsx";
import { Moon, Sun, Menu, User } from 'lucide-react';
import Logo2 from "../../../assets/Logo2.png";
import LoginModal from '../Home/Login';  // Import modal login

const Headers = ({ darkMode, toggleDarkMode, toggleSidebar }) => {
    const [isModalOpen, setIsModalOpen] = useState(false);  // State untuk modal login
    const location = useLocation();
    const excludedPaths = ["/", "/login"];
    const isExcludedPath = excludedPaths.includes(location.pathname);

    const toggleModal = () => {
        setIsModalOpen(!isModalOpen);  // Toggle modal login
    };

    return (
        <nav className={clsx("fixed top-0 z-50 w-full bg-white",
            "border-b border-gray-200 dark:bg-gray-800 dark:border-gray-700")}>
            <div className="px-2 py-3 lg:px-5 lg:pl-3">
                <div className="flex items-center justify-between">
                    <div className="flex items-center justify-start rtl:justify-end">
                        {!isExcludedPath && (
                            <button className={clsx("inline-flex items-center p-2 text-sm text-gray-500 rounded-lg",
                                "hover:bg-gray-100 focus:ring-gray-200",
                                "dark:text-gray-400 dark:hover:bg-gray-700 dark:focus:ring-gray-600"
                            )}
                                onClick={toggleSidebar}
                            >
                                <Menu className="text-2xl" />
                            </button>
                        )}
                        <a href="/#" className="flex items-center space-x-2">
                            <img src={Logo2} alt="Logo" width="60" height="50" />
                            <span className="self-center text-xl font-semibold sm:text-2xl whitespace-nowrap dark:text-white">SIOPS</span>
                        </a>
                    </div>
                    <div className="flex items-center space-x-4">
                        {isExcludedPath && (
                            <button 
                                className="flex items-center justify-center space-x-2 dark:bg-white w-10 h-10 rounded-full shadow-md"
                                onClick={toggleModal}  // Buka modal saat tombol diklik
                            >
                                <User size={24} className="dark:text-black" />
                            </button>
                        )}
                        <button className="dark:bg-slate-50 dark:text-slate-700 rounded-full p-2 font-bold"
                            onClick={toggleDarkMode}>
                            {darkMode ? <Sun /> : <Moon />}
                        </button>
                    </div>
                </div>
            </div>

            {/* Modal Login */}
            {isModalOpen && <LoginModal toggleModal={toggleModal} />}  {/* Render modal login */}
        </nav>
    );
};

export default Headers;
