// OrderStock.jsx
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const OrderStock = () => {
    const [orders, setOrders] = useState([]);
    const [newOrder, setNewOrder] = useState({
        noPembelian: '',
        date: '',
        kodebarang: '',
        barcode: '',
        namaBarang: '',
        hargaBeli: '',
        jumlahBeli: '',
        image: null
    });
    const [isEditing, setIsEditing] = useState(false);
    const [editId, setEditId] = useState(null);

    useEffect(() => {
        // Fetch orders on component mount
        const fetchOrders = async () => {
            try {
                const response = await axios.get('http://localhost:5000/orders');
                setOrders(response.data);
            } catch (error) {
                console.error('Error fetching orders:', error);
            }
        };

        fetchOrders();
    }, []);

    const handleInputChange = (e) => {
        const { name, value, type, files } = e.target;
        setNewOrder({
            ...newOrder,
            [name]: type === 'file' ? files[0] : value
        });
    };

    const handleAddOrder = async (e) => {
        e.preventDefault();
        try {
            const formData = new FormData();
            formData.append('noPembelian', newOrder.noPembelian);
            formData.append('date', newOrder.date);
            formData.append('kodebarang', newOrder.kodebarang);
            formData.append('barcode', newOrder.barcode);
            formData.append('namaBarang', newOrder.namaBarang);
            formData.append('hargaBeli', newOrder.hargaBeli);
            formData.append('jumlahBeli', newOrder.jumlahBeli);
            if (newOrder.image) formData.append('image', newOrder.image);

            if (isEditing) {
                await axios.patch(`http://localhost:5000/orders/${editId}`, formData, {
                    headers: { 'Content-Type': 'multipart/form-data' },
                });
                setOrders(orders.map(order => order.id === editId ? { ...newOrder, id: editId } : order));
                setIsEditing(false);
            } else {
                const response = await axios.post('http://localhost:5000/orders', formData, {
                    headers: { 'Content-Type': 'multipart/form-data' },
                });
                setOrders([...orders, response.data]);
            }

            // Reset form
            setNewOrder({
                noPembelian: '',
                date: '',
                kodebarang: '',
                barcode: '',
                namaBarang: '',
                hargaBeli: '',
                jumlahBeli: '',
                image: null
            });
        } catch (error) {
            console.error('Error adding/updating order:', error);
        }
    };

    const handleEdit = (order) => {
        setNewOrder(order);
        setEditId(order.id);
        setIsEditing(true);
    };

    const handleDelete = async (id) => {
        try {
            await axios.delete(`http://localhost:5000/orders/${id}`);
            setOrders(orders.filter(order => order.id !== id));
        } catch (error) {
            console.error('Error deleting order:', error);
        }
    };

    return (
        <div>
            <h1>Order Management</h1>
            <form onSubmit={handleAddOrder}>
                <input
                    type="text"
                    name="noPembelian"
                    placeholder="No Pembelian"
                    value={newOrder.noPembelian}
                    onChange={handleInputChange}
                    required
                />
                <input
                    type="date"
                    name="date"
                    placeholder="Date"
                    value={newOrder.date}
                    onChange={handleInputChange}
                    required
                />
                <input
                    type="text"
                    name="kodebarang"
                    placeholder="Kode Barang"
                    value={newOrder.kodebarang}
                    onChange={handleInputChange}
                    required
                />
                <input
                    type="text"
                    name="barcode"
                    placeholder="Barcode"
                    value={newOrder.barcode}
                    onChange={handleInputChange}
                    required
                />
                <input
                    type="text"
                    name="namaBarang"
                    placeholder="Nama Barang"
                    value={newOrder.namaBarang}
                    onChange={handleInputChange}
                    required
                />
                <input
                    type="number"
                    name="hargaBeli"
                    placeholder="Harga Beli"
                    value={newOrder.hargaBeli}
                    onChange={handleInputChange}
                    required
                />
                <input
                    type="number"
                    name="jumlahBeli"
                    placeholder="Jumlah Beli"
                    value={newOrder.jumlahBeli}
                    onChange={handleInputChange}
                    required
                />
                <input
                    type="file"
                    name="image"
                    onChange={handleInputChange}
                />
                <button type="submit">{isEditing ? 'Update Order' : 'Add Order'}</button>
            </form>
            <ul>
                {orders.map(order => (
                    <li key={order.id}>
                        <p>No Pembelian: {order.noPembelian}</p>
                        <p>Date: {new Date(order.date).toLocaleDateString()}</p>
                        <p>Kode Barang: {order.kodebarang}</p>
                        <p>Barcode: {order.barcode}</p>
                        <p>Nama Barang: {order.namaBarang}</p>
                        <p>Harga Beli: {order.hargaBeli}</p>
                        <p>Jumlah Beli: {order.jumlahBeli}</p>
                        {order.imageUrl && <img src={order.imageUrl} alt="Order" width="100" />}
                        <button onClick={() => handleEdit(order)}>Edit</button>
                        <button onClick={() => handleDelete(order.id)}>Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default OrderStock;
