import React, { useState, useEffect } from "react";
import clsx from "clsx";
import { Outlet } from "react-router-dom";
import SidebarAdmin from "../Sidebar/admin/SidebarAdmin"; // Admin Sidebar
import Sidebars from "../Sidebar/staff/Sidebars"; // Staff Sidebar
import Headers from "../Sidebar/Headers"; // Header Component

const DashboardLayout = () => {
  const [darkMode, setDarkMode] = useState(false);
  const [isMobileSidebarOpen, setIsMobileSidebarOpen] = useState(false); // Mobile sidebar
  const [isDesktopSidebarOpen, setIsDesktopSidebarOpen] = useState(true); // Desktop sidebar
  const [role, setRole] = useState(null); // State untuk role pengguna
  const SidebarComponent = role === "admin" ? SidebarAdmin : Sidebars;

  useEffect(() => {
    setRole(localStorage.getItem("role")); // Ambil role dari localStorage
  }, []);

  useEffect(() => {
    if (darkMode) {
      document.documentElement.classList.add("dark");
    } else {
      document.documentElement.classList.remove("dark");
    }
  }, [darkMode]);

  const toggleMobileSidebar = () => {
    setIsMobileSidebarOpen((prev) => !prev);
  };

  const toggleDesktopSidebar = () => {
    setIsDesktopSidebarOpen((prev) => !prev);
  };

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
  };

  return (
    <div className="flex min-h-screen bg-gray-100 dark:bg-gray-900">
      {/* Sidebar */}
      <div
        className={clsx(
          "fixed z-40 inset-y-0 left-0 bg-white dark:bg-gray-800 shadow-lg transition-transform duration-300 ease-in-out",
          isMobileSidebarOpen ? "translate-x-0 w-64" : "-translate-x-full", // Mobile
          isDesktopSidebarOpen ? "lg:translate-x-0 lg:w-64" : "lg:-translate-x-full" // Desktop
        )}
      >
        {SidebarComponent && (
          <SidebarComponent
            isSidebarOpen={isMobileSidebarOpen || isDesktopSidebarOpen}
            darkMode={darkMode}
            toggleDarkMode={toggleDarkMode}
          />
        )}
      </div>

      {/* Main Layout */}
      <div className="flex-1 flex flex-col transition-all duration-300 ease-in-out overflow-x-hidden">
        {/* Header */}
        <Headers
          toggleDarkMode={toggleDarkMode}
          darkMode={darkMode}
          toggleSidebar={toggleMobileSidebar} // Khusus mobile sidebar
        />

        {/* Content */}
        <main className="flex-1 p-4">
          <Outlet />
        </main>
      </div>
    </div>
  );
};

export default DashboardLayout;
