import React, { useState } from "react";
import clsx from "clsx";
import { Outlet } from "react-router-dom";
import SidebarAdmin from "../Sidebar/admin/SidebarAdmin"; // Admin Sidebar
import Sidebars from "../Sidebar/staff/Sidebars"; // Staff Sidebar
import Headers from "../Sidebar/Headers"; // Header Component

const DashboardLayout = () => {
  const [darkMode, setDarkMode] = useState(false);
  const [isSidebarOpen, setIsSidebarOpen] = useState(true); // Mengontrol sidebar

  // Fungsi untuk toggle sidebar kecil
  const toggleSidebar = () => {
    setIsSidebarOpen((prev) => !prev);
  };

  // Fungsi untuk toggle sidebar besar
  const toggleSidebarDesk = () => {
    setIsSidebarOpen((prev) => !prev); // Bisa menggunakan state yang sama
  };

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
  };

  return (
    <div className="flex min-h-screen bg-gray-100">
      {/* Sidebar */}
      <div
        className={clsx(
          "fixed z-40 inset-y-0 left-0 bg-white dark:bg-gray-800 shadow-lg transition-transform duration-300 ease-in-out",
          isSidebarOpen ? "translate-x-0 w-64" : "-translate-x-full", // Layar kecil
          "lg:translate-x-0 lg:w-64" // Sidebar selalu terbuka di desktop
        )}
      >
        <SidebarComponent
          isSidebarOpen={isSidebarOpen} // Tetap menggunakan satu state
          darkMode={darkMode}
          toggleDarkMode={toggleDarkMode}
        />
      </div>

      {/* Header */}
      <Headers
        toggleSidebar={toggleSidebar} // Untuk layar kecil
        toggleSidebarDesk={toggleSidebarDesk} // Untuk layar besar
        darkMode={darkMode}
        toggleDarkMode={toggleDarkMode}
      />

      {/* Main Content */}
      <div
        className={clsx(
          "flex-1 flex flex-col transition-all duration-300 ease-in-out overflow-x-hidden",
          isSidebarOpen ? "lg:ml-64" : "lg:ml-0" // Geser konten jika sidebar terbuka
        )}
      >
        <main className="flex-1 p-4">
          <Outlet />
        </main>
      </div>
    </div>
  );
};

export default DashboardLayout;
