import React, { useState } from "react";
import clsx from "clsx";
import { Outlet } from "react-router-dom";
import SidebarAdmin from "../Sidebar/admin/SidebarAdmin";
import Sidebars from "../Sidebar/staff/Sidebars";
import Headers from "../Sidebar/Headers";

const DashboardLayout = () => {
  const [darkMode, setDarkMode] = useState(false);
  const [isMobileSidebarOpen, setIsMobileSidebarOpen] = useState(false); // Mobile Sidebar
  const [isDesktopSidebarOpen, setIsDesktopSidebarOpen] = useState(true); // Desktop Sidebar
  const role = localStorage.getItem("role");
  const SidebarComponent = role === "admin" ? SidebarAdmin : Sidebars;

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
  };

  return (
    <div className="flex min-h-screen bg-gray-100">
      {/* Sidebar */}
      <div
        className={clsx(
          "fixed z-40 inset-y-0 left-0 bg-white dark:bg-gray-800 shadow-lg transition-transform duration-300 ease-in-out",
          isMobileSidebarOpen ? "translate-x-0 w-64" : "-translate-x-full", // Mobile
          isDesktopSidebarOpen && "hidden lg:block lg:w-64" // Desktop
        )}
      >
        <SidebarComponent
          isSidebarOpen={isMobileSidebarOpen || isDesktopSidebarOpen}
          darkMode={darkMode}
          toggleDarkMode={toggleDarkMode}
        />
      </div>

      {/* Header */}
      <div className={`${darkMode && "dark"}`}>
        <Headers
          toggleDarkMode={toggleDarkMode}
          darkMode={darkMode}
        />
      </div>

      {/* Main Content */}
      <div
        className={clsx(
          "flex-1 flex flex-col transition-all duration-300 ease-in-out overflow-x-hidden",
          isDesktopSidebarOpen ? "lg:ml-64" : "lg:ml-0"
        )}
      >
        <main className="flex-1 p-4">
          <Outlet />
        </main>
      </div>
    </div>
  );
};

export default DashboardLayout;
