import React from 'react';
import { AlertTriangle } from 'lucide-react';

const AlertModal = ({ show, message, onClose, isError }) => {
  if (!show) return null;

  const iconColor = isError ? 'text-red-600' : 'text-green-600';
  const iconBackground = isError ? 'bg-red-100' : 'bg-green-100';
  const buttonColor = isError ? 'bg-red-500 hover:bg-red-600' : 'bg-green-500 hover:bg-green-600';

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white p-6 rounded-lg shadow-lg max-w-sm w-full mx-4">
        <div className="text-center">
          <div className={`mx-auto flex items-center justify-center h-12 w-12 rounded-full ${iconBackground} mb-4`}>
            {isError ? (
              <AlertTriangle className={`h-6 w-6 ${iconColor}`} />
            ) : (
              <svg
                className={`h-6 w-6 ${iconColor}`}
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth="2"
                  d="M5 13l4 4L19 7"
                />
              </svg>
            )}
          </div>
          <h3 className="text-lg font-medium text-gray-900 mb-4">
            {message}
          </h3>
          <button
            onClick={onClose}
            className={`w-full ${buttonColor} text-white px-4 py-2 rounded-md focus:outline-none`}
          >
            {isError ? 'Close' : 'OK'}
          </button>
        </div>
      </div>
    </div>
  );
};

export default AlertModal;
