import React, { useState } from "react";
import clsx from "clsx";
import { Outlet } from "react-router-dom";
import SidebarAdmin from "../Sidebar/admin/SidebarAdmin";
import Headers from "../Sidebar/Headers";

const DashboardLayout = ({ toggleDarkMode, darkMode }) => {
    const [isSidebarOpen, setIsSidebarOpen] = useState(true); // Default terbuka
    const role = localStorage.getItem("role");

    const toggleSidebar = () => setIsSidebarOpen((prev) => !prev);

    return (
        <div className="flex min-h-screen">
            {/* Sidebar */}
            <div
                className={clsx(
                    "fixed z-40 inset-y-0 left-0 bg-white dark:bg-gray-800 shadow-lg transition-transform duration-300 ease-in-out",
                    isSidebarOpen ? "translate-x-0" : "-translate-x-full", // Mobile
                    "lg:translate-x-0 lg:w-64" // Selalu muncul di desktop
                )}
            >
                <SidebarAdmin darkMode={darkMode} toggleDarkMode={toggleDarkMode} />
            </div>

            {/* Konten Utama */}
            <div
                className={clsx(
                    "flex-1 transition-all duration-300 ease-in-out", // Animasi
                    isSidebarOpen ? "lg:ml-64" : "lg:ml-0" // Pergeseran konten
                )}
            >
                <Headers toggleDarkMode={toggleDarkMode} darkMode={darkMode} toggleSidebar={toggleSidebar} />
                <main className="p-4 bg-gray-100">
                    <Outlet />
                </main>
            </div>
        </div>
    );
};

export default DashboardLayout;
