  import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { 
  Package, 
  Users, 
  ShoppingCart, 
  TrendingUp, 
  Clipboard,
  Edit,
  Trash2,
  Plus,
  UserCircle
} from 'lucide-react';
import { useNavigate } from 'react-router-dom';

const UserModal = ({ user, isOpen, onClose, onSave, mode }) => {
  const [formData, setFormData] = useState({
    nama: user?.nama || '',
    email: user?.email || '',
    role: user?.role || 'staff'
  });

  useEffect(() => {
    if (user) {
      setFormData({
        nama: user.nama,
        email: user.email,
        role: user.role
      });
    }
  }, [user]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSave(formData);
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50">
      <div className="bg-white p-8 rounded-xl w-96">
        <h2 className="text-2xl font-bold mb-6">
          {mode === 'add' ? 'Add New User' : 'Edit User'}
        </h2>
        <form onSubmit={handleSubmit}>
          <div className="mb-4">
            <label className="block text-gray-700 mb-2">Name</label>
            <input
              type="text"
              name="nama"
              value={formData.nama}
              onChange={handleChange}
              className="w-full px-3 py-2 border rounded-lg"
              required
            />
          </div>
          <div className="mb-4">
            <label className="block text-gray-700 mb-2">Email</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              className="w-full px-3 py-2 border rounded-lg"
              required
            />
          </div>
          <div className="mb-4">
            <label className="block text-gray-700 mb-2">Role</label>
            <select
              name="role"
              value={formData.role}
              onChange={handleChange}
              className="w-full px-3 py-2 border rounded-lg"
            >
              <option value="staff">Staff</option>
              <option value="admin">Admin</option>
            </select>
          </div>
          <div className="flex justify-end space-x-4">
            <button
              type="button"
              onClick={onClose}
              className="px-4 py-2 bg-gray-200 text-gray-800 rounded-lg"
            >
              Cancel
            </button>
            <button
              type="submit"
              className="px-4 py-2 bg-blue-600 text-white rounded-lg"
            >
              Save
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

const DashboardAdmin = () => {
  const [orders, setOrders] = useState([]);
  const [users, setUsers] = useState([]);
  const [opnames, setOpnames] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [modalMode, setModalMode] = useState('add');
  const navigate = useNavigate();

  useEffect(() => {
    fetchOrderData();
    fetchUserData();
    fetchOpnameData();
  }, []);

  const fetchOrderData = async () => {
    try {
      const response = await axios.get('/api/orders');
      const sortedOrders = response.data.sort((a, b) => new Date(b.date) - new Date(a.date));
      setOrders(sortedOrders);  
    } catch (error) {
      console.error('Error fetching order data:', error);
    }
  };

  const fetchUserData = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        throw new Error('No token found');
      }

      const response = await axios.get('/api/users', {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });

      setUsers(response.data);
    } catch (error) {
      console.error('Error fetching user data:', error);
    }
  };

  const fetchOpnameData = async () => {
    try {
      const response = await axios.get('/api/opnames');
      setOpnames(response.data);
    } catch (error) {
      console.error('Error fetching opname data:', error);
    }
  };

  const handleAddUser = async (userData) => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post('/api/users', userData, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
      setUsers([...users, response.data]);
      setIsModalOpen(false);
    } catch (error) {
      console.error('Error adding user:', error);
    }
  };

  const handleEditUser = async (userData) => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.put(`/api/users/${selectedUser.id}`, userData, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
      setUsers(users.map(user => 
        user.id === selectedUser.id ? response.data : user
      ));
      setIsModalOpen(false);
    } catch (error) {
      console.error('Error editing user:', error);
    }
  };

  const handleDeleteUser = async (userId) => {
    try {
      const token = localStorage.getItem('token');
      await axios.delete(`/api/users/${userId}`, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
      setUsers(users.filter(user => user.id !== userId));
    } catch (error) {
      console.error('Error deleting user:', error);
    }
  };

  const openAddUserModal = () => {
    setSelectedUser(null);
    setModalMode('add');
    setIsModalOpen(true);
  };

  const openEditUserModal = (user) => {
    setSelectedUser(user);
    setModalMode('edit');
    setIsModalOpen(true);
  };

  const navigateToUserProfile = (userId) => {
    navigate(`/profile/${userId}`);
  };

  const calculateTotalStock = () => {
    return orders.reduce((total, order) => total + order.quantity, 0);
  };

  return (
    <div className="container mx-auto p-5 mt-12 bg-gray-50">
      <UserModal 
        user={selectedUser} 
        isOpen={isModalOpen} 
        onClose={() => setIsModalOpen(false)}
        onSave={modalMode === 'add' ? handleAddUser : handleEditUser}
        mode={modalMode}
      />

      <h2 className="text-3xl font-bold mb-6 text-gray-800">Stock Management Dashboard</h2>

      {/* Summary Cards */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
        <div className="bg-white p-6 rounded-xl shadow-md hover:shadow-lg transition-all">
          <div className="flex items-center justify-between">
            <div>
              <h3 className="text-lg font-semibold text-gray-600">Total Orders</h3>
              <p className="text-3xl font-bold text-blue-600">{orders.length}</p>
            </div>
            <ShoppingCart className="text-blue-500" size={40} />
          </div>
        </div>

        <div className="bg-white p-6 rounded-xl shadow-md hover:shadow-lg transition-all">
          <div className="flex items-center justify-between">
            <div>
              <h3 className="text-lg font-semibold text-gray-600">Total Stock</h3>
              <p className="text-3xl font-bold text-green-600">{calculateTotalStock()}</p>
            </div>
            <Package className="text-green-500" size={40} />
          </div>
        </div>

        <div className="bg-white p-6 rounded-xl shadow-md hover:shadow-lg transition-all">
          <div className="flex items-center justify-between">
            <div>
              <h3 className="text-lg font-semibold text-gray-600">Opname Records</h3>
              <p className="text-3xl font-bold text-purple-600">{opnames.length}</p>
            </div>
            <Clipboard className="text-purple-500" size={40} />
          </div>
        </div>
      </div>

      {/* Recent Orders and User Management */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Recent Orders Table */}
        <div className="bg-white p-6 rounded-xl shadow-md">
          <div className="flex justify-between items-center mb-4">
            <h3 className="text-xl font-semibold text-gray-800">Recent Orders</h3>
            <TrendingUp className="text-blue-500" size={24} />
          </div>
          <div className="overflow-x-auto">
            <table className="w-full text-sm text-left">
              <thead className="bg-gray-100 text-gray-600">
                <tr>
                  <th className="p-3">Nama Barang</th>
                  <th className="p-3">Quantity</th>
                  <th className="p-3">Tanggal</th>
                </tr>
              </thead>
              <tbody>
                {orders.slice(0, 5).map(order => (
                  <tr key={order.id} className="border-b hover:bg-gray-50">
                    <td className="p-3">{order.namaBarang}</td>
                    <td className="p-3">{order.quantity}</td>
                    <td className="p-3">{new Date(order.date).toLocaleDateString()}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>

        {/* User Management */}
        <div className="bg-white p-6 rounded-xl shadow-md">
          <div className="flex justify-between items-center mb-4">
            <h3 className="text-xl font-semibold text-gray-800">User Management</h3>
            <div className="flex items-center space-x-2">
              <button 
                onClick={openAddUserModal}
                className="text-green-500 hover:bg-green-50 p-2 rounded-full"
                title="Add New User"
              >
                <Plus size={24} />
              </button>
              <Users className="text-green-500" size={24} />
            </div>
          </div>
          <div className="overflow-x-auto">
            <table className="w-full text-sm text-left">
              <thead className="bg-gray-100 text-gray-600">
                <tr>
                  <th className="p-3">Nama</th>
                  <th className="p-3">Email</th>
                  <th className="p-3">Role</th>
                  <th className="p-3">Actions</th>
                </tr>
              </thead>
              <tbody>
                {users.map(user => (
                  <tr key={user.id} className="border-b hover:bg-gray-50">
                    <td 
                      className="p-3 cursor-pointer hover:text-blue-600" 
                      onClick={() => navigateToUserProfile(user.id)}
                    >
                      <div className="flex items-center">
                        <UserCircle className="mr-2" size={20} />
                        {user.nama}
                      </div>
                    </td>
                    <td className="p-3">{user.email}</td>
                    <td className="p-3">{user.role}</td>
                    <td className="p-3 flex space-x-2">
                      <button 
                        onClick={() => openEditUserModal(user)}
                        className="text-blue-500 hover:bg-blue-50 p-1 rounded-full"
                        title="Edit User"
                      >
                        <Edit size={16} />
                      </button>
                      <button 
                        onClick={() => handleDeleteUser(user.id)}
                        className="text-red-500 hover:bg-red-50 p-1 rounded-full"
                        title="Delete User"
                      >
                        <Trash2 size={16} />
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default DashboardAdmin;
