import React, { useState } from "react";
import clsx from "clsx";
import { Outlet } from "react-router-dom";
import SidebarAdmin from "../Sidebar/admin/SidebarAdmin"; // Admin Sidebar
import Sidebars from "../Sidebar/staff/Sidebars"; // Staff Sidebar
import Headers from "../Sidebar/Headers"; // Header Component

const DashboardLayout = () => {
  const [darkMode, setDarkMode] = useState(false);
  const [isSmallMenuOpen, setIsSmallMenuOpen] = useState(true); // Untuk layar kecil
  const [isLargeMenuOpen, setIsLargeMenuOpen] = useState(true); // Untuk layar besar
  const role = localStorage.getItem("role"); // Ambil role dari localStorage
  const SidebarComponent = role === "admin" ? SidebarAdmin : Sidebars;

  // Fungsi toggle sidebar
  const toggleSidebar = (menuType) => {
    if (menuType === "small") {
      setIsSmallMenuOpen((prev) => !prev);
    } else if (menuType === "large") {
      setIsLargeMenuOpen((prev) => !prev);
    }
  };

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
  };

  return (
    <div className="flex min-h-screen bg-gray-100">
      {/* Sidebar */}
      <div
        className={clsx(
          "fixed z-40 inset-y-0 left-0 bg-white dark:bg-gray-800 shadow-lg transition-transform duration-300 ease-in-out",
          isSmallMenuOpen ? "translate-x-0 w-64" : "-translate-x-full", // Layar kecil
          isLargeMenuOpen ? "lg:translate-x-0 lg:w-64" : "lg:w-0 lg:hidden" // Layar besar
        )}
      >
        <SidebarComponent
          isSidebarOpen={isSmallMenuOpen || isLargeMenuOpen} // Terbuka jika salah satu menu terbuka
          darkMode={darkMode}
          toggleDarkMode={toggleDarkMode}
        />
      </div>

      {/* Header */}
      <Headers
        toggleDarkMode={toggleDarkMode}
        darkMode={darkMode}
        toggleSidebar={toggleSidebar}
      />

      {/* Main Content Area */}
      <div
        className={clsx(
          "flex-1 flex flex-col transition-all duration-300 ease-in-out overflow-x-hidden",
          isLargeMenuOpen ? "lg:ml-64" : "lg:ml-0" // Geser main content jika sidebar besar terbuka
        )}
      >
        <main className="flex-1 p-4">
          <Outlet />
        </main>
      </div>
    </div>
  );
};

export default DashboardLayout;
