const DashboardLayout = ({ children, toggleDarkMode, darkMode }) => {
    const [isSidebarOpen, setIsSidebarOpen] = useState(false); // Kontrol sidebar
    const role = localStorage.getItem("role");
    const SidebarComponent = role === "admin" ? SidebarAdmin : Sidebars;

    // Toggle sidebar visibility
    const toggleSidebar = () => {
        setIsSidebarOpen((prev) => !prev);
    };

    return (
        <div className="flex min-h-screen">
            {/* Sidebar - Hidden on mobile, visible on desktop */}
            <div className="hidden lg:flex">
                <SidebarComponent
                    isSidebarOpen={true} // Always open in desktop mode
                    darkMode={darkMode}
                    toggleDarkMode={toggleDarkMode}
                />
            </div>

            {/* Mobile Sidebar - Controlled by the toggle button */}
            <div
                className={`fixed z-40 inset-y-0 left-0 transform ${
                    isSidebarOpen ? "translate-x-0" : "-translate-x-full"
                } transition-transform duration-300 ease-in-out lg:hidden`}
            >
                <SidebarComponent
                    isSidebarOpen={isSidebarOpen} // Open/Close in mobile mode
                    darkMode={darkMode}
                    toggleDarkMode={toggleDarkMode}
                />
            </div>

            {/* Main Content */}
            <div className="flex-1 flex flex-col">
                {/* Header */}
                <Headers
                    toggleDarkMode={toggleDarkMode}
                    darkMode={darkMode}
                    toggleSidebar={toggleSidebar} // Oper fungsi ke Header
                />
                <main className="flex-1 p-4 lg:ml-64 bg-gray-100">
                    <Outlet />
                </main>
            </div>
        </div>
    );
};

export default DashboardLayout;
