import React from 'react';
import { AlertTriangle, CheckCircle } from 'lucide-react';

const AlertModal = ({ 
  show = false, 
  onClose, 
  message = "Terjadi kesalahan", 
  isError = true 
}) => {
  if (!show) return null;

  return (
    <div 
      className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-[9999]"
      onClick={onClose}
    >
      <div 
        className="bg-white rounded-xl shadow-lg p-6 max-w-md w-full mx-4 text-center relative"
        onClick={(e) => e.stopPropagation()}
      >
        <div className={`mx-auto flex items-center justify-center h-12 w-12 rounded-full ${isError ? 'bg-red-100' : 'bg-green-100'} mb-4`}>
          {isError ? (
            <AlertTriangle className="h-6 w-6 text-red-600" />
          ) : (
            <CheckCircle className="h-6 w-6 text-green-600" />
          )}
        </div>
        <h3 className={`text-lg font-medium ${isError ? 'text-red-900' : 'text-green-900'} mb-4`}>
          {message}
        </h3>
        <button
          onClick={onClose}
          className={`w-full px-4 py-2 rounded-md focus:outline-none ${
            isError 
              ? 'bg-red-500 text-white hover:bg-red-600' 
              : 'bg-green-500 text-white hover:bg-green-600'
          }`}
        >
          {isError ? 'Tutup' : 'OK'}
        </button>
      </div>
    </div>
  );
};

export default AlertModal;
