import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Logo2 from "../../../assets/Logo2.png";
import { motion } from 'framer-motion';
import axios from 'axios';
import { jwtDecode } from 'jwt-decode';
import { Eye, EyeOff } from 'lucide-react';

const Login = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:5000/users/login', {
        email,
        password
      });

      const { token, user } = response.data;

      if (!token) {
        throw new Error('Token not received from server');
      }

      // Store token and user info
      localStorage.setItem('token', token);
      localStorage.setItem('user', JSON.stringify(user));

      // Set default authorization header for all future requests
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;

      // Decode token and redirect based on role
      const decodedToken = jwtDecode(token);
      if (decodedToken.role === 'admin') {
        navigate('/DashboardAdmin');
      } else if (decodedToken.role === 'staff') {
        navigate('/dashboard');
      }

    } catch (error) {
      console.error('Login error:', error);
      if (error.response) {
        switch (error.response.status) {
          case 400:
            setError('Password salah!');
            break;
          case 404:
            setError('User tidak ditemukan!');
            break;
          default:
            setError('Terjadi kesalahan pada server.');
        }
      } else {
        setError('Terjadi kesalahan pada koneksi.');
      }
    }
  };

  return (
    <div className="flex justify-center items-center h-screen bg-gray-100 dark:bg-gray-800 relative">
      <div className="bg-white dark:bg-gray-100 rounded-[30px] shadow-lg flex flex-col md:flex-row w-[90%] max-w-[900px] h-auto md:h-[500px] overflow-hidden">
        <motion.div
          initial={{ opacity: 0, x: -200 }}
          animate={{ opacity: 1, x: 0 }}
          exit={{ opacity: 0, x: -200 }}
          className="flex-1 bg-white dark:bg-gray-100 p-6 md:p-10 flex flex-col justify-center"
        >
          <img src={Logo2} alt="Logo" className="h-20 w-20 md:h-28 md:w-32 mx-auto" />
          <h2 className="text-2xl md:text-3xl font-bold mb-4 md:mb-6 mx-auto">
            {isLogin ? 'Login' : 'Reset Password'}
          </h2>

          <form className="space-y-4" onSubmit={handleLogin}>
            {isLogin ? (
              <>
                <input
                  type="email"
                  placeholder="Enter your Email"
                  value={email}
                  autoComplete="username"
                  onChange={(e) => setEmail(e.target.value)}
                  className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500"
                />
                <div className="relative">
                  <input
                    type={showPassword ? "text" : "password"}
                    placeholder="Password"
                    value={password}
                    autoComplete="current-password"
                    onChange={(e) => setPassword(e.target.value)}
                    className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500"
                  />
                  <button
                    type="button"
                    onClick={() => setShowPassword(!showPassword)}
                    className="absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-500 hover:text-black"
                  >
                    {showPassword ? <EyeOff size={20} /> : <Eye size={20} />}
                  </button>
                </div>
                {error && <p className="text-red-500">{error}</p>}
                <button
                  type="submit"
                  className="w-full bg-purple-600 text-white p-3 rounded-lg hover:bg-purple-700 focus:outline-none focus:ring-2 focus:ring-purple-500"
                >
                  Login
                </button>
              </>
            ) : (
              <button
                type="button"
                onClick={() => setIsLogin(true)}
                className="w-full bg-purple-600 text-white p-3 rounded-lg hover:bg-purple-700 focus:outline-none focus:ring-2 focus:ring-purple-500"
              >
                Back to Login
              </button>
            )}
          </form>
        </motion.div>
        <motion.div
          initial={{ opacity: 0, x: 200 }}
          animate={{ opacity: 1, x: 0 }}
          exit={{ opacity: 0, x: 200 }}
          className="flex-1 bg-purple-600 p-6 md:p-10 flex flex-col justify-center text-white"
        >
          <h2 className="text-2xl md:text-3xl font-bold mb-4">Welcome to SIOPS</h2>
          <p className="mb-6">Please log in to access the system.</p>
        </motion.div>
      </div>
    </div>
  );
};

export default Login;
