import React, { useState, useEffect, useCallback } from "react";
import clsx from "clsx";
import { Moon, Sun, Menu, User, UserCircle, LogOut, Settings } from "lucide-react";
import Logo2 from "../../../assets/Logo2.png";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Headers = ({ darkMode, toggleDarkMode, toggleSidebar, toggleDesktopSidebar }) => {
  const [menuActive, setMenuActive] = useState(false);
  const [userDropdown, setUserDropdown] = useState(false);
  const [userData, setUserData] = useState(null);
  const navigate = useNavigate();

  const fetchUserProfile = useCallback(async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        console.error('No token found');
        navigate('/');
        return;
      }

      const response = await axios.get('http://localhost:5000/users/profile', {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });

      setUserData(response.data);
    } catch (error) {
      console.error('Error fetching user profile:', error);
      if (error.response?.status === 403 || error.response?.status === 401) {
        localStorage.removeItem('token');
        navigate('/');
      }
    }
  }, [navigate]);

  useEffect(() => {
    fetchUserProfile();
  }, [fetchUserProfile]);

  const handleLogout = async () => {
    try {
      const token = localStorage.getItem('token');
      if (token) {
        await axios.post('http://localhost:5000/users/logout', {}, {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });
      }
    } catch (error) {
      console.error('Logout error:', error);
    } finally {
      localStorage.removeItem('token');
      navigate('/');
    }
  };

  const toggleDropdown = () => {
    setUserDropdown(!userDropdown);
  };

  return (
    <nav
      className={clsx(
        "fixed top-0 z-50 w-full bg-white",
        "border-b border-gray-200 dark:bg-gray-800 dark:border-gray-700"
      )}
    >
      <div className="px-2 py-3 lg:px-5 lg:pl-3">
        <div className="flex items-center justify-between">
          <div className="flex items-center justify-start rtl:justify-end">
            {/* Mobile menu button */}
            <button
              data-drawer-target="logo-sidebar"
              data-drawer-toggle="logo-sidebar"
              aria-controls="logo-sidebar"
              type="button"
              className="inline-flex items-center p-2 text-sm text-gray-500 rounded-lg lg:hidden hover:bg-gray-100 focus:outline-none focus:ring-2 focus:ring-gray-200 dark:text-gray-400 dark:hover:bg-gray-700 dark:focus:ring-gray-600"
              onClick={toggleSidebar}
            >
              <span className="sr-only">Open sidebar</span>
              <Menu className="w-6 h-6" />
            </button>

            {/* Logo */}
            <a href="/#" className="flex items-center space-x-2 ml-2">
              <img src={Logo2} alt="Logo" width="60" height="50" />
              <span className="self-center text-xl font-semibold sm:text-2xl whitespace-nowrap dark:text-white">
                SIOPS
              </span>
            </a>

            {/* Desktop menu button */}
            <button
              className={clsx(
                menuActive ? "" : "mx-20",
                "items-center p-2 text-sm text-gray-500 rounded-lg hidden lg:inline-flex",
                "hover:bg-gray-100 focus:outline-none focus:ring-2 focus:ring-gray-200",
                "dark:text-gray-400 dark:hover:bg-gray-700 dark:focus:ring-gray-600"
              )}
              onClick={() => {
                toggleDesktopSidebar();
                setMenuActive(!menuActive);
              }}
            >
              <Menu className="w-6 h-6" />
            </button>
          </div>

          {/* Right side buttons */}
          <div className="flex items-center relative">
            <div className="relative">
              <button 
                onClick={toggleDropdown}
                onMouseEnter={() => setUserDropdown(true)}
                onMouseLeave={() => setUserDropdown(false)}
                className={clsx(
                  "flex items-center justify-center space-x-2",
                  "w-10 h-10 rounded-full shadow-md transition-colors duration-200",
                  "hover:bg-gray-100 dark:hover:bg-gray-700",
                  "bg-white dark:bg-gray-800"
                )}
              >
                <User size={24} className="text-gray-600 dark:text-gray-300" />
              </button>

              {/* Dropdown with explicit conditions and debugging */}
              {userDropdown && (
                <div 
                  className="absolute right-0 top-full mt-2 w-64 bg-white dark:bg-gray-800 rounded-lg shadow-lg border dark:border-gray-700"
                  onMouseEnter={() => setUserDropdown(true)}
                  onMouseLeave={() => setUserDropdown(false)}
                >
                  {userData ? (
                    <>
                      <div className="p-4 border-b dark:border-gray-700 flex items-center">
                        <UserCircle size={40} className="mr-3 text-gray-500 dark:text-gray-400" />
                        <div>
                          <p className="text-sm font-semibold dark:text-white">
                            {userData.nama || 'User Name'}
                          </p>
                          <p className="text-xs text-gray-500 dark:text-gray-400 capitalize">
                            {userData.role || 'Role'}
                          </p>
                        </div>
                      </div>
                      <div className="py-1">
                        <button 
                          onClick={() => navigate('/profile')}
                          className="w-full text-left px-4 py-2 text-sm hover:bg-gray-100 dark:hover:bg-gray-700 flex items-center dark:text-white"
                        >
                          <UserCircle size={16} className="mr-2 text-gray-500 dark:text-gray-400" />
                          Profile
                        </button>
                        <button 
                          onClick={() => navigate('/settings')}
                          className="w-full text-left px-4 py-2 text-sm hover:bg-gray-100 dark:hover:bg-gray-700 flex items-center dark:text-white"
                        >
                          <Settings size={16} className="mr-2 text-gray-500 dark:text-gray-400" />
                          Settings
                        </button>
                        <button 
                          onClick={handleLogout}
                          className="w-full text-left px-4 py-2 text-sm hover:bg-gray-100 dark:hover:bg-gray-700 flex items-center text-red-500 dark:text-red-400"
                        >
                          <LogOut size={16} className="mr-2" />
                          Logout
                        </button>
                      </div>
                    </>
                  ) : (
                    <div className="p-4 text-center text-gray-500">
                      Unable to load user profile
                    </div>
                  )}
                </div>
              )}
            </div>

            <button
              onClick={toggleDarkMode}
              type="button"
              className="flex items-center justify-center p-2 text-sm text-gray-500 rounded-lg hover:bg-gray-100 focus:outline-none focus:ring-2 focus:ring-gray-200 dark:text-gray-400 dark:hover:bg-gray-700 dark:focus:ring-gray-600 ml-2"
            >
              {darkMode ? (
                <Sun className="w-5 h-5" />
              ) : (
                <Moon className="w-5 h-5" />
              )}
            </button>
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Headers;
