  import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Clipboard, Box, Users, ShoppingCart } from 'react-feather';

const DashboardAdmin = () => {
  const [orders, setOrders] = useState([]);
  const [users, setUsers] = useState([]);

  useEffect(() => {
    fetchOrderData();
    fetchUserData();
  }, []);

  const fetchOrderData = async () => {
    try {
      const response = await axios.get('/api/orders');
      setOrders(response.data);
    } catch (error) {
      console.error('Error fetching order data:', error);
    }
  };

  const fetchUserData = async () => {
    try {
      const response = await axios.get('/api/users');
      setUsers(response.data);
    } catch (error) {
      console.error('Error fetching user data:', error);
    }
  };

  const calculateTotalStock = () => {
    return orders.reduce((total, order) => total + (order.quantity || 0), 0);
  };

  return (
    <div className="container mx-auto p-5 mt-12 bg-gray-50">
      <h2 className="text-3xl font-bold mb-6 text-gray-800">Stock Management Dashboard</h2>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {/* Orders Card */}
        <div className="bg-white p-6 rounded-lg shadow-md">
          <div className="flex items-center justify-between">
            <div>
              <h3 className="text-lg font-semibold text-gray-600">Total Orders</h3>
              <p className="text-3xl font-bold text-blue-600">{orders.length}</p>
            </div>
            <ShoppingCart className="text-blue-500" size={40} />
          </div>
        </div>

        {/* Users Card */}
        <div className="bg-white p-6 rounded-lg shadow-md">
          <div className="flex items-center justify-between">
            <div>
              <h3 className="text-lg font-semibold text-gray-600">Total Users</h3>
              <p className="text-3xl font-bold text-green-600">{users.length}</p>
            </div>
            <Users className="text-green-500" size={40} />
          </div>
        </div>

        {/* Stock Card */}
        <div className="bg-white p-6 rounded-lg shadow-md">
          <div className="flex items-center justify-between">
            <div>
              <h3 className="text-lg font-semibold text-gray-600">Total Stock</h3>
              <p className="text-3xl font-bold text-purple-600">{calculateTotalStock()}</p>
            </div>
            <Box className="text-purple-500" size={40} />
          </div>
        </div>
      </div>

      {/* Additional sections can be added here */}
    </div>
  );
};

export default DashboardAdmin;
