import React, { useState } from "react";
import clsx from "clsx";
import { Outlet } from "react-router-dom";
import SidebarAdmin from "../Sidebar/admin/SidebarAdmin"; // Admin Sidebar
import Sidebars from "../Sidebar/staff/Sidebars"; // Staff Sidebar
import Headers from "../Sidebar/Headers"; // Header Component

const DashboardLayout = () => {
  const [darkMode, setDarkMode] = useState(false);
  const [isSmallScreenSidebarOpen, setIsSmallScreenSidebarOpen] = useState(true);
  const [isLargeScreenSidebarOpen, setIsLargeScreenSidebarOpen] = useState(true);
  const role = localStorage.getItem("role"); // Ambil role dari localStorage
  const SidebarComponent = role === "admin" ? SidebarAdmin : Sidebars;

  const toggleSmallScreenSidebar = () => {
    setIsSmallScreenSidebarOpen((prev) => !prev);
  };

  const toggleLargeScreenSidebar = () => {
    setIsLargeScreenSidebarOpen((prev) => !prev);
  };

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
  };

  return (
    <div className="flex min-h-screen bg-gray-100">
      {/* Sidebar */}
      <div
        className={clsx(
          "fixed z-40 inset-y-0 left-0 bg-white dark:bg-gray-800 shadow-lg transition-transform duration-300 ease-in-out",
          isSmallScreenSidebarOpen ? "translate-x-0 w-64" : "-translate-x-full", // Mobile
          isLargeScreenSidebarOpen ? "lg:translate-x-0 lg:w-64" : "lg:w-0 lg:hidden" // Desktop
        )}
      >
        <SidebarComponent
          isSidebarOpen={isLargeScreenSidebarOpen || isSmallScreenSidebarOpen}
          darkMode={darkMode}
          toggleDarkMode={toggleDarkMode}
        />
      </div>

      {/* Header */}
      <Headers
        toggleDarkMode={toggleDarkMode}
        darkMode={darkMode}
        toggleSmallScreenSidebar={toggleSmallScreenSidebar}
        toggleLargeScreenSidebar={toggleLargeScreenSidebar}
      />

      {/* Main Content Area */}
      <div
        className={clsx(
          "flex-1 flex flex-col transition-all duration-300 ease-in-out overflow-x-hidden",
          isLargeScreenSidebarOpen ? "lg:ml-64" : "lg:ml-0" // Geser main content jika sidebar terbuka
        )}
      >
        <main className="flex-1 p-4">
          <Outlet />
        </main>
      </div>
    </div>
  );
};

export default DashboardLayout;
