import React, { useEffect, useState } from 'react';

import axios from 'axios';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

const OrderChart = () => {
  const [chartData, setChartData] = useState({});

  useEffect(() => {
    fetchOrderData();
  }, []);

  const fetchOrderData = async () => {
    try {
      const response = await axios.get('http://localhost:5000/orders');
      const orders = response.data;

      // Group orders by date
      const groupedOrders = orders.reduce((acc, order) => {
        const date = new Date(order.date).toLocaleDateString();
        if (!acc[date]) {
          acc[date] = [];
        }
        acc[date].push(order);
        return acc;
      }, {});

      // Prepare data for the chart
      const dates = Object.keys(groupedOrders);
      const orderCounts = dates.map(date => groupedOrders[date].length);

      setChartData({
        labels: dates,
        datasets: [
          {
            label: 'Jumlah Order per Tanggal',
            data: orderCounts,
            borderColor: 'rgba(75, 192, 192, 1)',
            backgroundColor: 'rgba(75, 192, 192, 0.2)',
            fill: true,
          },
        ],
      });
    } catch (error) {
      console.error('Error fetching order data:', error);
    }
  };

  return (
    <div className="container mx-auto p-5 mt-5">
      <h2 className="text-2xl font-bold mb-4">Order Chart</h2>
      <div>
        {chartData.labels ? (
          <Line data={chartData} />
        ) : (
          <p>Loading chart data...</p>
        )}
      </div>
    </div>
  );
};

export default OrderChart;
