import React, { useState } from 'react';
import Logo2 from "../../../assets/Logo2.png";

const Login = () => {
  const [isActive, setIsActive] = useState(false);

  const handleResetClick = () => {
    setIsActive(true);
  };

  const handleLoginClick = () => {
    setIsActive(false);
  };

  return (
    <div className="flex justify-center items-center h-screen bg-gray-100 dark:bg-gray-800">
      <div className={`bg-white rounded-lg shadow-lg flex w-[900px] h-[500px] overflow-hidden relative transition-all duration-700 ${isActive ? 'transform translate-x-[-50%]' : ''}`}>
        
        {/* Left Side - Sign In */}
        <div className={`flex-1 bg-gray-100 dark:bg-gray-700 p-10 flex flex-col justify-center transition-all duration-700 ${isActive ? 'translate-x-full opacity-0' : 'opacity-100'}`}>
          <img src={Logo2} alt="Logo" className="h-28 w-32 mx-auto" />
          <h2 className="text-3xl font-bold mb-6 mx-auto">Login</h2>

          {/* Sign In Form */}
          <form className="space-y-4">
            <input
              type="email"
              placeholder="Enter your Email"
              className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500"
            />
            <input
              type="password"
              placeholder="Password"
              className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500"
            />
            <button
              type="submit"
              className="w-full py-3 bg-purple-600 dark:bg-gradient-to-br dark:from-purple-800 dark:to-blue-900 text-white rounded-lg hover:bg-purple-700 transition duration-300"
            >
              Sign In
            </button>
          </form>
        </div>

        {/* Right Side - Sign Up / Reset */}
        <div className={`flex-1 bg-gradient-to-r from-purple-600 to-purple-400 dark:bg-gradient-to-br dark:from-purple-800 dark:to-blue-900 text-white p-10 flex flex-col justify-center items-center transition-all duration-700 ${isActive ? 'translate-x-0 opacity-100' : 'opacity-0 -translate-x-full'}`}>
          <h2 className="text-3xl font-bold mb-4">Hello, Friend!</h2>
          <p className="mb-6 text-center">Enter your email to reset your password.</p>
          <button
            onClick={handleLoginClick}
            className="py-3 px-8 bg-white dark:bg-purple-600 text-purple-600 dark:text-white rounded-lg hover:bg-gray-100 transition duration-300"
          >
            Back to Login
          </button>
        </div>
        
        {/* Transition Button */}
        <button
          onClick={handleResetClick}
          className={`absolute left-1/2 transform -translate-x-1/2 bottom-10 py-3 px-8 bg-purple-600 text-white rounded-lg hover:bg-purple-700 transition duration-300 ${isActive ? 'hidden' : ''}`}
        >
          Reset
        </button>
      </div>
    </div>
  );
};

export default Login;
