import React, { useState } from "react";
import clsx from "clsx";
import { Outlet } from "react-router-dom";
import SidebarAdmin from "../Sidebar/admin/SidebarAdmin";
import Sidebars from "../Sidebar/staff/Sidebars";
import Headers from "../Sidebar/Headers";

const DashboardLayout = () => {
  const [darkMode, setDarkMode] = useState(false); // Untuk dark mode
  const [isSidebarOpen, setIsSidebarOpen] = useState(false); // Untuk sidebar mobile
  const [isDesktopSidebarOpen, setIsDesktopSidebarOpen] = useState(true); // Untuk sidebar desktop
  const role = localStorage.getItem("role"); // Role user (admin/staff)
  const SidebarComponent = role === "admin" ? SidebarAdmin : Sidebars;

  // Toggle mobile sidebar
  const toggleSidebar = () => {
    setIsSidebarOpen((prev) => !prev); // Mengubah state mobile sidebar
  };

  // Toggle desktop sidebar
  const toggleDesktopSidebar = () => {
    setIsDesktopSidebarOpen((prev) => !prev); // Mengubah state desktop sidebar
  };

  // Toggle dark mode
  const toggleDarkMode = () => {
    setDarkMode((prev) => !prev);
  };

  return (
    <div className={clsx("flex min-h-screen", darkMode && "dark")}>
      {/* Sidebar */}
      <div
        className={clsx(
          "fixed z-40 inset-y-0 left-0 bg-white dark:bg-gray-800 shadow-lg transition-transform duration-300 ease-in-out",
          isSidebarOpen ? "translate-x-0 w-64" : "-translate-x-full", // Mobile: buka/tutup
          "lg:translate-x-0 lg:w-64", // Desktop: default terbuka
          !isDesktopSidebarOpen && "lg:w-0" // Desktop: tutup jika state berubah
        )}
      >
        <SidebarComponent darkMode={darkMode} />
      </div>

      {/* Header */}
      <Headers
        darkMode={darkMode}
        toggleDarkMode={toggleDarkMode}
        toggleSidebar={toggleSidebar} // Mobile sidebar toggle
        isDesktopSidebarOpen={isDesktopSidebarOpen}
        toggleDesktopSidebar={toggleDesktopSidebar} // Desktop sidebar toggle
      />

      {/* Main Content */}
      <div
        className={clsx(
          "flex-1 flex flex-col transition-all duration-300 ease-in-out overflow-x-hidden",
          isDesktopSidebarOpen ? "lg:ml-64" : "lg:ml-0" // Geser konten jika sidebar desktop terbuka
        )}
      >
        <main className="flex-1 p-4">
          <Outlet />
        </main>
      </div>
    </div>
  );
};

export default DashboardLayout;
