  import React, { useState, useEffect } from "react";
import axios from "axios";
import { 
  FilePlus, 
  Search, 
  Trash2, 
  Package, 
  Calendar, 
  Barcode, 
  ShoppingCart, 
  DollarSign 
} from "lucide-react";
import CrudButton from "../../Button/CrudButton";
import { motion, AnimatePresence } from "framer-motion";
import DeleteModal from "../../modal/DeleteModal";

const OrderStock = () => {
  const [orders, setOrders] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [newOrder, setNewOrder] = useState({
    noPembelian: "",
    date: "",
    kodebarang: "",
    barcode: "",
    namaBarang: "",
    hargaBeli: "",
    jumlahBeli: "",
  });
  const [isEditing, setIsEditing] = useState(false);
  const [editId, setEditId] = useState(null);

  // New state for delete modal
  const [deleteOrder, setDeleteOrder] = useState(null);

  useEffect(() => {
    fetchOrders();
  }, []);

  const fetchOrders = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        throw new Error('No authentication token found');
      }

      const response = await axios.get("http://localhost:5000/orders", {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
      const sortedOrders = response.data.sort(
        (a, b) => new Date(b.date) - new Date(a.date)
      );
      setOrders(sortedOrders);
    } catch (error) {
      console.error("Error fetching orders:", error.response ? error.response.data : error.message);
    }
  };

  const handleAddOrder = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        throw new Error('No authentication token found');
      }

      const payload = {
        noPembelian: newOrder.noPembelian,
        date: newOrder.date,
        kodebarang: newOrder.kodebarang,
        barcode: newOrder.barcode,
        namaBarang: newOrder.namaBarang,
        hargaBeli: parseInt(newOrder.hargaBeli, 10),
        jumlahBeli: parseInt(newOrder.jumlahBeli, 10),
      };

      if (isEditing) {
        await axios.patch(`http://localhost:5000/orders/${editId}`, payload, {
          headers: { 
            "Content-Type": "application/json",
            "Authorization": `Bearer ${token}`
          },
        });
        setOrders(
          orders.map((order) =>
            order.id === editId ? { ...newOrder, id: editId } : order
          )
        );
        setIsEditing(false);
      } else {
        const response = await axios.post(
          "http://localhost:5000/orders",
          payload,
          {
            headers: { 
              "Content-Type": "application/json",
              "Authorization": `Bearer ${token}`
            },
          }
        );
        setOrders([...orders, { ...payload, id: response.data.id }]);
      }

      setNewOrder({
        noPembelian: "",
        date: "",
        kodebarang: "",
        barcode: "",
        namaBarang: "",
        hargaBeli: "",
        jumlahBeli: "",
      });
    } catch (error) {
      console.error("Error adding/updating order:", error.response ? error.response.data : error.message);
    }
  };

  const handleEditOrder = (id) => {
    const orderToEdit = orders.find((order) => order.id === id);
    setNewOrder(orderToEdit);
    setEditId(id);
    setIsEditing(true);
  };

  // Modified delete method to use DeleteModal
  const handleDeleteOrderConfirmation = (order) => {
    setDeleteOrder(order);
  };

  const confirmDeleteOrder = async () => {
    if (!deleteOrder) return;

    try {
      const token = localStorage.getItem('token');
      if (!token) {
        throw new Error('No authentication token found');
      }

      await axios.delete(`http://localhost:5000/orders/${deleteOrder.id}`, {
        headers: {
          "Authorization": `Bearer ${token}`
        }
      });

      // Remove the order from the list
      setOrders(orders.filter((order) => order.id !== deleteOrder.id));

      // Close the delete modal
      setDeleteOrder(null);
    } catch (error) {
      console.error("Error deleting order:", error.response ? error.response.data : error.message);
      // Optionally, you could show an error message here
      setDeleteOrder(null);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewOrder({ ...newOrder, [name]: value });
  };

  const filteredOrders = orders.filter((order) => 
    order.kodebarang.toLowerCase().includes(searchTerm.toLowerCase()) ||
    order.namaBarang.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="container mx-auto p-6 pt-20 bg-gray-50 min-h-screen">
      {/* Delete Confirmation Modal */}
      {deleteOrder && (
        <DeleteModal
          title="Hapus Order"
          message={`Apakah Anda yakin ingin menghapus order ${deleteOrder.namaBarang}?`}
          onConfirm={confirmDeleteOrder}
          onCancel={() => setDeleteOrder(null)}
        />
      )}

      <motion.div 
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
        className="bg-white shadow-xl rounded-2xl overflow-hidden"
      >
        {/* Header with Stats */}
        <div className="bg-gradient-to-r from-blue-600 to-indigo-700 text-white p-6">
          <div className="flex justify-between items-center">
            <h1 className="text-3xl font-bold tracking-wide">Order Stok Management</h1>
            <div className="flex space-x-4">
              <div className="flex items-center bg-white/20 p-3 rounded-lg">
                <ShoppingCart className="mr-2" />
                <span className="font-semibold">{orders.length} Total Orders</span>
              </div>
              <div className="flex items-center bg-white/20 p-3 rounded-lg">
                <Package className="mr-2" />
                <span className="font-semibold">
                  {orders.reduce((total, order) => total + order.jumlahBeli, 0)} Items
                </span>
              </div>
            </div>
          </div>
        </div>

        {/* Order Form */}
        <div className="p-6 bg-gray-100">
          <form 
            onSubmit={handleAddOrder} 
            className="grid grid-cols-1 md:grid-cols-3 gap-4"
          >
            <div className="relative">
              <label className="block text-sm font-medium text-gray-700 mb-1">No Pembelian</label>
              <input
                type="text"
                name="noPembelian"
                value={newOrder.noPembelian}
                onChange={handleInputChange}
                required
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 transition"
                placeholder="Nomor Pembelian"
              />
            </div>
            <div className="relative">
              <label className="block text-sm font-medium text-gray-700 mb-1">Tanggal</label>
              <div className="flex items-center">
                <Calendar className="absolute left-3 text-gray-500" size={20} />
                <input
                  type="date"
                  name="date"
                  value={newOrder.date}
                  onChange={handleInputChange}
                  required
                  className="w-full pl-10 px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 transition"
                />
              </div>
            </div>
            <div className="relative">
              <label className="block text-sm font-medium text-gray-700 mb-1">Kode Barang</label>
              <input
                type="text"
                name="kodebarang"
                value={newOrder.kodebarang}
                onChange={handleInputChange}
                required
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 transition"
                placeholder="Kode Barang"
              />
            </div>
            <div className="relative">
              <label className="block text-sm font-medium text-gray-700 mb-1">Barcode</label>
              <div className="flex items-center">
                <Barcode  className="absolute left-3 text-gray-500" size={20} />
                <input
                  type="text"
                  name="barcode"
                  value={newOrder.barcode}
                  onChange={handleInputChange}
                  required
                  className="w-full pl-10 px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 transition"
                  placeholder="Barcode Produk"
                />
              </div>
            </div>
            <div className="relative">
              <label className="block text-sm font-medium text-gray-700 mb-1">Nama Barang</label>
              <input
                type="text"
                name="namaBarang"
                value={newOrder.namaBarang}
                onChange={handleInputChange}
                required
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 transition"
                placeholder="Nama Barang"
              />
            </div>
            <div className="relative">
              <label className="block text-sm font-medium text-gray-700 mb-1">Harga Beli</label>
              <div className="flex items-center">
                <DollarSign className="absolute left-3 text-gray-500" size={20} />
                <input
                  type="number"
                  name="hargaBeli"
                  value={newOrder.hargaBeli}
                  onChange={handleInputChange}
                  required
                  className="w-full pl-10 px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 transition"
                  placeholder="Harga Beli"
                />
              </div>
            </div>
            <div className="relative">
              <label className="block text-sm font-medium text-gray-700 mb-1">Jumlah Beli</label>
              <input
                type="number"
                name="jumlahBeli"
                value={newOrder.jumlahBeli}
                onChange={handleInputChange}
                required
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 transition"
                placeholder="Jumlah Beli"
              />
            </div>
            <div className="flex items-end">
              <button 
                type="submit" 
                className="w-full bg-blue-600 text-white py-2 rounded-lg hover:bg-blue-700 transition flex items-center justify-center"
              >
                {isEditing ? (
                  <>
                    <FilePlus className="mr-2" /> Update Order
                  </>
                ) : (
                  <>
                    <ShoppingCart className="mr-2" /> Tambah Order
                  </>
                )}
              </button>
            </div>
          </form>
        </div>

        {/* Search and Table */}
        <div className="p-6">
          <div className="mb-4 flex justify-between items-center">
            <div className="relative w-full max-w-md">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={20} />
              <input
                type="text"
                placeholder="Cari Kode Barang atau Nama Barang"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 transition"
              />
            </div>
          </div>

          <div className="overflow-x-auto">
            <table className="w-full bg-white shadow-md rounded-lg overflow-hidden">
              <thead className="bg-gray-200 text-gray-600 uppercase text-sm leading-normal">
                <tr>
                  {['No Pembelian', 'Tanggal', 'Kode Barang', 'Barcode', 'Nama Barang', 'Harga Beli', 'Jumlah Beli', 'Aksi'].map((header) => (
                    <th key={header} className="py-3 px-4 text-left">{header}</th>
                  ))}
                </tr>
              </thead>
              <tbody className="text-gray-600 text-sm font-light">
                {filteredOrders.map((order) => (
                  <tr 
                    key={order.id} 
                    className="border-b border-gray-200 hover:bg-gray-100 transition"
                  >
                    <td className="py-3 px-4">{order.noPembelian}</td>
                    <td className="py-3 px-4">{new Date(order.date).toLocaleDateString()}</td>
                    <td className="py-3 px-4">{order.kodebarang}</td>
                    <td className="py-3 px-4">{order.barcode}</td>
                    <td className="py-3 px-4">{order.namaBarang}</td>
                    <td className="py-3 px-4">Rp {order.hargaBeli.toLocaleString()}</td>
                    <td className="py-3 px-4">{order.jumlahBeli}</td>
                    <td className="py-3 px-4 flex space-x-2">
                      <motion.button
                        whileHover={{ scale: 1.05 }}
                        whileTap={{ scale: 0.95 }}
                        onClick={() => handleEditOrder(order.id)}
                        className="text-blue-500 hover:text-blue-700 transition"
                      >
                        <FilePlus size={20} />
                      </motion.button>
                      <motion.button
                        whileHover={{ scale: 1.05 }}
                        whileTap={{ scale: 0.95 }}
                        onClick={() => handleDeleteOrderConfirmation(order)}
                        className="text-red-500 hover:text-red-700 transition"
                      >
                        <Trash2 size={20} />
                      </motion.button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
            {filteredOrders.length === 0 && (
              <div className="text-center py-6 text-gray-500">
                Tidak ada data order yang ditemukan
              </div>
            )}
          </div>
        </div>
      </motion.div>
    </div>
  );
};

export default OrderStock;
