import React, { useState } from 'react';
import { motion } from 'framer-motion';
import Logo2 from "../../../assets/Logo2.png";
import { ArrowBigLeft } from "lucide-react";
import Buttons from '../../Button/Buttons';
import { useNavigate } from 'react-router-dom';  // If using react-router

const Login = () => {
  const [isLogin, setIsLogin] = useState(true);
  const navigate = useNavigate();  // For React Router navigation

  // Function to go back to home or any route
  const goHome = () => {
    navigate('/');  // Navigate to the home page or use any route
  };

  return (
    <div className="flex justify-center items-center h-screen bg-gray-100 dark:bg-gray-800 relative">

      {/* Custom Button using Buttons component for "Go Back Home" */}
      <Buttons
        icon={<ArrowBigLeft className="h-5 w-5" />}  // Pass the icon here
        label="Home"  // Optional label
        mode="primary"  // You can change this to "outline" or any other mode
        size="lg"  // Button size
        className="absolute top-5 left-5 flex items-center"  // Position the button
        onClick={goHome}  // Navigate to home when clicked
      />

      <div className="bg-white dark:bg-gray-100 rounded-[30px] shadow-lg flex w-[900px] h-[500px] overflow-hidden">
        
        {/* Left Side - Form */}
        <motion.div
          initial={{ opacity: 0, x: -200 }}
          animate={{ opacity: 1, x: 0 }}
          exit={{ opacity: 0, x: -200 }}
          className="flex-1 bg-white dark:bg-gray-100 p-10 flex flex-col justify-center"
        >
          <img src={Logo2} alt="Logo" className="h-28 w-32 mx-auto" />
          <h2 className="text-3xl font-bold mb-6 mx-auto">
            {isLogin ? 'Login' : 'Reset Password'}
          </h2>

          {/* Conditional Form */}
          <form className="space-y-4">
            {isLogin ? (
              <>
                <input
                  type="email"
                  placeholder="Enter your Email"
                  className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500"
                />
                <input
                  type="password"
                  placeholder="Password"
                  className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500"
                />
                <button
                  type="submit"
                  className="w-full py-3 bg-purple-600 dark:bg-gradient-to-br dark:from-purple-800 dark:to-blue-900 text-white rounded-lg hover:bg-purple-700 transition duration-300"
                >
                  Sign In
                </button>
              </>
            ) : (
              <>
                <input
                  type="email"
                  placeholder="Enter your Email to reset password"
                  className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500"
                />
                <button
                  type="submit"
                  className="w-full py-3 bg-purple-600 dark:bg-gradient-to-br dark:from-purple-800 dark:to-blue-900 text-white rounded-lg hover:bg-purple-700 transition duration-300"
                >
                  Reset Password
                </button>
              </>
            )}
          </form>
        </motion.div>

        {/* Right Side - Toggle Button */}
        <motion.div
          initial={{ opacity: 0, x: 200 }}
          animate={{ opacity: 1, x: 0 }}
          exit={{ opacity: 0, x: 200 }}
          className="flex-1 bg-gradient-to-r from-purple-600 to-purple-400 dark:bg-gradient-to-br dark:from-purple-800 dark:to-blue-900 text-white p-10 flex flex-col justify-center items-center rounded-[150px_0_0_100px]"
        >
          <h2 className="text-3xl font-bold mb-4">
            {isLogin ? 'Forget Password?' : 'Back to Login'}
          </h2>
          <p className="mb-6 text-center">
            {isLogin
              ? 'Register with your personal details to use all of the site\'s features'
              : 'Go back to login to access your account'}
          </p>
          <button
            onClick={() => setIsLogin(!isLogin)}
            className="py-3 px-8 bg-white dark:bg-purple-600 text-purple-600 dark:text-white rounded-lg hover:bg-gray-100 transition duration-300"
          >
            {isLogin ? 'Reset' : 'Login'}
          </button>
        </motion.div>
      </div>
    </div>
  );
};

export default Login;
