// AddOrder.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AddOrder = ({ orderToEdit, onSave, onCancel }) => {
  const [newOrder, setNewOrder] = useState({
    noPembelian: '',
    date: '',
    kodeBarang: '',
    barcode: '',
    namaBarang: '',
    hargaBeli: '',
    image: null,
  });

  useEffect(() => {
    if (orderToEdit) {
      setNewOrder(orderToEdit);
    } else {
      setNewOrder({
        noPembelian: '',
        date: '',
        kodeBarang: '',
        barcode: '',
        namaBarang: '',
        hargaBeli: '',
        image: null,
      });
    }
  }, [orderToEdit]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewOrder({ ...newOrder, [name]: value });
  };

  const handleImageUpload = (e) => {
    setNewOrder({ ...newOrder, image: e.target.files[0] });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const formData = new FormData();
      for (const key in newOrder) {
        formData.append(key, newOrder[key]);
      }
      const url = orderToEdit ? `http://localhost:5000/orders/${orderToEdit.id}` : 'http://localhost:5000/orders';
      const method = orderToEdit ? 'patch' : 'post';

      await axios[method](url, formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      onSave();
    } catch (error) {
      console.error('Error saving order:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="mb-5">
      <div className="grid grid-cols-2 gap-4">
        <input
          type="text"
          name="noPembelian"
          placeholder="No Pembelian"
          value={newOrder.noPembelian}
          onChange={handleInputChange}
          required
          className="border p-2"
        />
        <input
          type="date"
          name="date"
          placeholder="Date"
          value={newOrder.date}
          onChange={handleInputChange}
          required
          className="border p-2"
        />
        <input
          type="text"
          name="kodeBarang"
          placeholder="Kode Barang"
          value={newOrder.kodeBarang}
          onChange={handleInputChange}
          required
          className="border p-2"
        />
        <input
          type="text"
          name="barcode"
          placeholder="Barcode"
          value={newOrder.barcode}
          onChange={handleInputChange}
          required
          className="border p-2"
        />
        <input
          type="text"
          name="namaBarang"
          placeholder="Nama Barang"
          value={newOrder.namaBarang}
          onChange={handleInputChange}
          required
          className="border p-2"
        />
        <input
          type="number"
          name="hargaBeli"
          placeholder="Harga Beli"
          value={newOrder.hargaBeli}
          onChange={handleInputChange}
          required
          className="border p-2"
        />
        <input
          type="file"
          name="image"
          onChange={handleImageUpload}
          className="border p-2"
        />
      </div>
      <button type="submit" className="bg-blue-500 text-white p-2 mt-4">
        {orderToEdit ? 'Update Order' : 'Tambah Order'}
      </button>
      <button type="button" onClick={onCancel} className="bg-gray-500 text-white p-2 mt-4 ml-2">
        Cancel
      </button>
    </form>
  );
};

export default AddOrder;
