import React, { useState } from "react";
import clsx from "clsx";
import { Outlet } from "react-router-dom";
import SidebarAdmin from "../Sidebar/admin/SidebarAdmin";
import Sidebars from "../Sidebar/staff/Sidebars";
import Headers from "../Sidebar/Headers";

const DashboardLayout = () => {
  const [darkMode, setDarkMode] = useState(false);
  const [isSidebarOpen, setIsSidebarOpen] = useState(false); // Untuk mobile
  const [isDesktopSidebarOpen, setIsDesktopSidebarOpen] = useState(true); // Untuk desktop
  const role = localStorage.getItem("role"); // Ambil role dari localStorage
  const SidebarComponent = role === "admin" ? SidebarAdmin : Sidebars;

  const toggleSidebar = () => {
    setIsSidebarOpen((prev) => !prev); // Mobile toggle
  };

  const toggleDesktopSidebar = () => {
    setIsDesktopSidebarOpen((prev) => !prev); // Desktop toggle
  };

  const toggleDarkMode = () => {
    setDarkMode((prev) => !prev);
  };

  return (
    <div className="flex min-h-screen bg-gray-100">
      {/* Sidebar */}
      <div
        className={clsx(
          "fixed z-40 inset-y-0 left-0 bg-white dark:bg-gray-800 shadow-lg transition-transform duration-300 ease-in-out",
          isSidebarOpen || isDesktopSidebarOpen ? "translate-x-0 w-64" : "-translate-x-full", // Sesuaikan state untuk mobile dan desktop
          "lg:translate-x-0 lg:w-64", // Default terbuka di desktop
          !isDesktopSidebarOpen && "lg:w-0" // Tertutup di desktop
        )}
      >
        <SidebarComponent darkMode={darkMode} />
      </div>

      {/* Header */}
      <Headers
        darkMode={darkMode}
        toggleDarkMode={toggleDarkMode}
        toggleSidebar={toggleSidebar}
        isDesktopSidebarOpen={isDesktopSidebarOpen}
        toggleDesktopSidebar={toggleDesktopSidebar}
      />

      {/* Main Content */}
      <div
        className={clsx(
          "flex-1 flex flex-col transition-all duration-300 ease-in-out overflow-x-hidden",
          isDesktopSidebarOpen ? "lg:ml-64" : "lg:ml-0" // Geser konten jika sidebar desktop terbuka
        )}
      >
        <main className="flex-1 p-4">
          <Outlet />
        </main>
      </div>
    </div>
  );
};

export default DashboardLayout;
