import React, { useEffect, useRef, useState } from 'react';
import axios from 'axios';
import Chart from 'chart.js/auto';

const DashboardStaff = () => {
  const [orders, setOrders] = useState([]);
  const chartRef = useRef(null);
  const chartInstance = useRef(null);

  useEffect(() => {
    fetchOrderData();
    return () => {
      if (chartInstance.current) {
        chartInstance.current.destroy();
      }
    };
  }, []);

  const fetchOrderData = async () => {
    try {
      const response = await axios.get('http://localhost:5000/orders');
      const orders = response.data;
      setOrders(orders);

      if (orders.length === 0) {
        return; // Tidak membuat chart jika tidak ada data pesanan
      }

      const groupedOrders = orders.reduce((acc, order) => {
        const date = new Date(order.date).toLocaleDateString();
        if (!acc[date]) {
          acc[date] = [];
        }
        acc[date].push(order);
        return acc;
      }, {});

      const dates = Object.keys(groupedOrders);
      const orderCounts = dates.map(date => groupedOrders[date].length);

      if (chartInstance.current) {
        chartInstance.current.destroy();
      }

      const ctx = chartRef.current.getContext('2d');
      chartInstance.current = new Chart(ctx, {
        type: 'line',
        data: {
          labels: dates,
          datasets: [
            {
              label: 'Sales',
              data: orderCounts,
              borderColor: 'rgba(75, 192, 192, 1)',
              backgroundColor: 'rgba(75, 192, 192, 0.2)',
              fill: true,
            },
          ],
        },
        options: {
          responsive: true,
          plugins: {
            legend: {
              display: true,
            },
            tooltip: {
              callbacks: {
                label: function(context) {
                  return `Tanggal: ${context.label}, Jumlah: ${context.raw}`;
                }
              }
            }
          },
          scales: {
            x: {
              grid: {
                display: true,
                color: 'rgba(200, 200, 200, 0.2)',
              },
            },
            y: {
              grid: {
                display: true,
                color: 'rgba(200, 200, 200, 0.2)',
              },
            },
          },
          animation: {
            duration: 1000,
            easing: 'easeInOutQuad',
          },
        },
      });
    } catch (error) {
      console.error('Error fetching order data:', error);
    }
  };

  return (
    <div className="container mx-auto p-5 mt-5">
      <h2 className="text-2xl font-bold mb-4">Dashboard</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        <div className="bg-white p-4 rounded-lg shadow-md">
          <h3 className="text-xl font-semibold mb-2">Total Orders</h3>
          <p className="text-3xl">{orders.length}</p>
        </div>
        <div className="bg-white p-4 rounded-lg shadow-md">
          <h3 className="text-xl font-semibold mb-2">Total Sales</h3>
          <p className="text-3xl">${orders.reduce((sum, order) => sum + order.hargaBeli, 0)}</p>
        </div>
      </div>
      <div className="bg-white p-4 rounded-lg shadow-md mt-4">
        <h3 className="text-xl font-semibold mb-2">Sales Chart</h3>
        <canvas ref={chartRef} />
      </div>
      <div className="bg-white p-4 rounded-lg shadow-md mt-4">
        <h3 className="text-xl font-semibold mb-2">Recent Orders</h3>
        <ul>
          {orders.slice(0, 5).map(order => (
            <li key={order.id} className="border-b py-2">
              {order.namaBarang} - {new Date(order.date).toLocaleDateString()}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default DashboardStaff;
