import React from "react";
import SidebarAdmin from "../components/Page/Sidebar/admin/SidebarAdmin";
import Sidebars from "../components/Page/Sidebar/staff/Sidebars";
import Headers from "../Sidebar/Headers";

const DashboardLayout = ({ children, toggleDarkMode, darkMode, toggleSidebar }) => {
  const role = localStorage.getItem("role");
  const SidebarComponent = role === "admin" ? SidebarAdmin : Sidebars;

  return (
    <div className="flex">
      <SidebarComponent />
      <div className="flex-1 min-h-screen">
        <Headers toggleDarkMode={toggleDarkMode} darkMode={darkMode} toggleSidebar={toggleSidebar} />
        <main className="p-4">{children}</main>
      </div>
    </div>
  );
};

export default DashboardLayout;
