import React, { useState, useEffect, useCallback } from "react";
import clsx from "clsx";
import { Moon, Sun, Menu, User, UserCircle, LogOut, Settings } from "lucide-react";
import Logo2 from "../../../assets/Logo2.png";
import axios from "../../../config/axios";
import { useNavigate } from "react-router-dom";

const Headers = ({ darkMode, toggleDarkMode, toggleSidebar, toggleDesktopSidebar }) => {
  const [menuActive, setMenuActive] = useState(false);
  const [userDropdown, setUserDropdown] = useState(false);
  const [userData, setUserData] = useState(null);
  const navigate = useNavigate();

  const fetchUserProfile = useCallback(async () => {
    try {
      const response = await axios.get('/users/profile');
      setUserData(response.data);
    } catch (error) {
      console.error('Error fetching user profile:', error);
      if (error.response?.status === 401 || error.response?.status === 403) {
        localStorage.removeItem('token');
        navigate('/login');
      }
    }
  }, [navigate]);

  useEffect(() => {
    fetchUserProfile();
  }, [fetchUserProfile]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  const toggleDropdown = () => {
    setUserDropdown(!userDropdown);
  };

  return (
    <header className="fixed top-0 z-50 w-full bg-white dark:bg-gray-900 shadow-sm">
      <div className="flex items-center justify-between h-16 px-4 sm:px-6">
        <div className="flex items-center">
          <button
            onClick={toggleSidebar}
            className="p-2 rounded-md text-gray-400 hover:text-gray-500 hover:bg-gray-100 focus:outline-none focus:ring-2 focus:ring-inset focus:ring-indigo-500 md:hidden"
          >
            <Menu size={24} />
          </button>
          <button
            onClick={toggleDesktopSidebar}
            className="hidden md:block p-2 rounded-md text-gray-400 hover:text-gray-500 hover:bg-gray-100 focus:outline-none focus:ring-2 focus:ring-inset focus:ring-indigo-500"
          >
            <Menu size={24} />
          </button>
          <img src={Logo2} alt="Logo" className="h-8 w-auto ml-2" />
        </div>

        <div className="flex items-center space-x-4">
          <button
            onClick={toggleDarkMode}
            className="p-2 rounded-md text-gray-400 hover:text-gray-500 hover:bg-gray-100 focus:outline-none focus:ring-2 focus:ring-inset focus:ring-indigo-500"
          >
            {darkMode ? <Sun size={24} /> : <Moon size={24} />}
          </button>

          <div className="relative">
            <button
              onClick={toggleDropdown}
              className="flex items-center space-x-2 p-2 rounded-md text-gray-400 hover:text-gray-500 hover:bg-gray-100 focus:outline-none focus:ring-2 focus:ring-inset focus:ring-indigo-500"
            >
              <UserCircle size={24} />
              {userData && (
                <span className="hidden md:block text-sm font-medium text-gray-700 dark:text-gray-200">
                  {userData.name}
                </span>
              )}
            </button>

            {userDropdown && (
              <div className="absolute right-0 mt-2 w-48 rounded-md shadow-lg bg-white dark:bg-gray-800 ring-1 ring-black ring-opacity-5">
                <div className="py-1">
                  <button
                    onClick={handleLogout}
                    className="flex items-center w-full px-4 py-2 text-sm text-gray-700 dark:text-gray-200 hover:bg-gray-100 dark:hover:bg-gray-700"
                  >
                    <LogOut size={16} className="mr-2" />
                    Logout
                  </button>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </header>
  );
};

export default Headers;
