  import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { 
  Users, 
  UserPlus,
  Pencil,
  Trash2
} from 'lucide-react';
import UserModal from '../../../modal/UserModal.jsx';

import SuccessModal from '../../../modal/SuccessModal.jsx';


const DashboardAdmin = () => {
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isDeleteModalOpen, setIsDeleteModalOpen] = useState(false);
  const [role, setRole] = useState('');
  const [successModalOpen, setSuccessModalOpen] = useState(false);
  const [errorModalOpen, setErrorModalOpen] = useState(false);
  const [modalMessage, setModalMessage] = useState('');

  useEffect(() => {
    fetchUserData();
    getRole();
  }, []);

  const getRole = () => {
    const token = localStorage.getItem('token');
    if (token) {
      const userData = JSON.parse(atob(token.split('.')[1]));
      setRole(userData.role);
    }
  };

  const fetchUserData = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        throw new Error('No token found');
      }

      const response = await axios.get('http://localhost:5000/users', {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
      setUsers(response.data);
    } catch (error) {
      console.error('Error fetching user data:', error.response ? error.response.data : error.message);
      setModalMessage('Gagal mengambil data staff');
      setErrorModalOpen(true);
    }
  };

  const handleAddUser = async (userData) => {
    try {
      const token = localStorage.getItem('token');
      await axios.post('http://localhost:5000/users', {
        ...userData,
        role: 'staff'
      }, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
      setIsModalOpen(false);
      fetchUserData();
      setModalMessage('Staff berhasil ditambahkan');
      setSuccessModalOpen(true);
    } catch (error) {
      setModalMessage(error.response?.data?.msg || 'Gagal menambahkan staff');
      setErrorModalOpen(true);
    }
  };

  const handleUpdateUser = async (userData) => {
    if (!selectedUser) {
      console.error('No user selected for update');
      return;
    }

    try {
      const token = localStorage.getItem('token');
      const dataToUpdate = { 
        ...userData,
        role: 'staff'
      };

      if (!dataToUpdate.password) {
        delete dataToUpdate.password;
      }

      await axios.put(`http://localhost:5000/users/${selectedUser.user_id}`, dataToUpdate, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
      setIsModalOpen(false);
      fetchUserData();
      setModalMessage('Staff berhasil diperbarui');
      setSuccessModalOpen(true);
    } catch (error) {
      setModalMessage(error.response?.data?.msg || 'Gagal memperbarui staff');
      setErrorModalOpen(true);
    }
  };

  const handleDeleteUser = async (userId) => {
    setIsDeleteModalOpen(false);

    try {
      const token = localStorage.getItem('token');
      await axios.delete(`http://localhost:5000/users/${userId}`, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
      fetchUserData();
      setModalMessage('Staff berhasil dihapus');
      setSuccessModalOpen(true);
    } catch (error) {
      setModalMessage(error.response?.data?.msg || 'Gagal menghapus staff');
      setErrorModalOpen(true);
    }
  };

  return (
    <div className="container mx-auto p-5 mt-16 bg-gray-50">
      <h2 className="text-3xl font-bold mb-6 text-gray-800">Staff Management Dashboard</h2>

      {/* Staff Management */}
      <div className="bg-white p-6 rounded-xl shadow-md">
        <div className="flex justify-between items-center mb-4">
          <h3 className="text-xl font-semibold text-gray-800">Staff Management</h3>
          <div className="flex items-center space-x-2">
            {role === 'admin' && (
              <button
                onClick={() => {
                  setSelectedUser(null);
                  setIsModalOpen(true);
                }}
                className="flex items-center px-3 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
              >
                <UserPlus className="h-5 w-5 mr-1" />
                Add Staff
              </button>
            )}
            <Users className="text-green-500" size={24} />
          </div>
        </div>
        <div className="overflow-x-auto">
          <table className="w-full text-sm text-left">
            <thead className="bg-gray-100 text-gray-600">
              <tr>
                <th className="p-3">Nama</th>
                <th className="p-3">Email</th>
                <th className="p-3">Role</th>
                {role === 'admin' && <th className="p-3">Actions</th>}
              </tr>
            </thead>
            <tbody>
              {users
                .filter(user => user.role === 'staff')
                .map((user, index) => (
                  <tr key={`staff-${user.user_id || index}`} className="border-b hover:bg-gray-50">
                    <td className="p-3">{user.name}</td>
                    <td className="p-3">{user.email}</td>
                    <td className="p-3 capitalize">{user.role}</td>
                    {role === 'admin' && (
                      <td className="p-3">
                        <div className="flex space-x-2">
                          <button
                            onClick={() => {
                              setSelectedUser(user);
                              setIsModalOpen(true);
                            }}
                            className="p-1 text-blue-600 hover:text-blue-800"
                          >
                            <Pencil className="h-5 w-5" />
                          </button>
                          <button
                            onClick={() => {
                              setSelectedUser(user);
                              setIsDeleteModalOpen(true);
                            }}
                            className="p-1 text-red-600 hover:text-red-800"
                          >
                            <Trash2 className="h-5 w-5" />
                          </button>
                        </div>
                      </td>
                    )}
                  </tr>
                ))}
            </tbody>
          </table>
        </div>
      </div>

      {/* Modals */}
      <UserModal
        isOpen={isModalOpen}
        onClose={() => setIsModalOpen(false)}
        onSubmit={selectedUser ? handleUpdateUser : handleAddUser}
        user={selectedUser}
      />


      <SuccessModal
        isOpen={successModalOpen}
        onClose={() => setSuccessModalOpen(false)}
        message={modalMessage}
      />


    </div>
  );
};

export default DashboardAdmin;
