  import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { 
  Package, 
  Users, 
  ShoppingCart, 
  TrendingUp, 
  Clipboard,
  FilePlus,
  Edit,
  Trash2
} from 'lucide-react';
import { useNavigate } from 'react-router-dom';
import CrudButton from '../../../Button/CrudButton';
import UserModal from '../../../modal/UserModal.jsx';
import SuccessModal from '../../../modal/SuccessModal.jsx';
import AlertModal from '../../../modal/AlertModal.jsx';

const DashboardAdmin = () => {
  const [orders, setOrders] = useState([]);
  const [users, setUsers] = useState([]);
  const [role, setRole] = useState('');
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedUser, setSelectedUser] = useState(null);
  const [modalMode, setModalMode] = useState('add');
  const [successModalOpen, setSuccessModalOpen] = useState(false);
  const [errorModalOpen, setErrorModalOpen] = useState(false);
  const [modalMessage, setModalMessage] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/');
      return;
    }

    // Set default axios authorization header
    axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;

    // Verify token and set role
    try {
      const userData = JSON.parse(atob(token.split('.')[1]));
      if (userData.role !== 'admin') {
        localStorage.removeItem('token');
        navigate('/');
        return;
      }
      setRole(userData.role);
    } catch (error) {
      console.error('Invalid token:', error);
      localStorage.removeItem('token');
      navigate('/');
      return;
    }

    fetchOrderData();
    fetchUserData();
  }, [navigate]);

  const fetchOrderData = async () => {
    try {
      const response = await axios.get('http://localhost:5000/orders');
      const sortedOrders = response.data.sort(
        (a, b) => new Date(b.tgl_order) - new Date(a.tgl_order)
      );
      setOrders(sortedOrders);
    } catch (error) {
      console.error('Error fetching order data:', error.response?.data || error.message);
      if (error.response?.status === 403 || error.response?.status === 401) {
        localStorage.removeItem('token');
        navigate('/');
      }
    }
  };

  const fetchUserData = async () => {
    try {
      const response = await axios.get('http://localhost:5000/users');
      setUsers(response.data);
    } catch (error) {
      console.error('Error fetching user data:', error.response?.data || error.message);
      if (error.response?.status === 403 || error.response?.status === 401) {
        localStorage.removeItem('token');
        navigate('/');
      } else {
        setModalMessage('Gagal mengambil data staff');
        setErrorModalOpen(true);
      }
    }
  };

  const handleAddUser = async (userData) => {
    try {
      await axios.post('http://localhost:5000/users', {
        ...userData,
        role: 'staff'
      });
      setIsModalOpen(false);
      fetchUserData();
      setModalMessage('Staff berhasil ditambahkan');
      setSuccessModalOpen(true);
    } catch (error) {
      if (error.response?.status === 403 || error.response?.status === 401) {
        localStorage.removeItem('token');
        navigate('/');
      } else {
        setModalMessage(error.response?.data?.msg || 'Gagal menambahkan staff');
        setErrorModalOpen(true);
      }
    }
  };

  const handleEditUser = async (userData) => {
    if (!selectedUser?.user_id) return;

    try {
      await axios.patch(`http://localhost:5000/users/${selectedUser.user_id}`, userData);
      setIsModalOpen(false);
      fetchUserData();
      setModalMessage('Staff berhasil diupdate');
      setSuccessModalOpen(true);
    } catch (error) {
      if (error.response?.status === 403 || error.response?.status === 401) {
        localStorage.removeItem('token');
        navigate('/');
      } else {
        setModalMessage(error.response?.data?.msg || 'Gagal mengupdate staff');
        setErrorModalOpen(true);
      }
    }
  };

  const handleDeleteUser = async (userId) => {
    try {
      await axios.delete(`http://localhost:5000/users/${userId}`);
      fetchUserData();
      setModalMessage('Staff berhasil dihapus');
      setSuccessModalOpen(true);
    } catch (error) {
      if (error.response?.status === 403 || error.response?.status === 401) {
        localStorage.removeItem('token');
        navigate('/');
      } else {
        setModalMessage(error.response?.data?.msg || 'Gagal menghapus staff');
        setErrorModalOpen(true);
      }
    }
  };

  const openAddModal = () => {
    setSelectedUser(null);
    setModalMode('add');
    setIsModalOpen(true);
  };

  const openEditModal = (user) => {
    setSelectedUser(user);
    setModalMode('edit');
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setSelectedUser(null);
  };

  return (
    <div className="p-6 pt-20">
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mb-8">
        <div className="bg-white p-6 rounded-lg shadow-md">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-gray-500">Total Orders</p>
              <h3 className="text-2xl font-bold">{orders.length}</h3>
            </div>
            <ShoppingCart className="text-blue-500" size={24} />
          </div>
        </div>

        <div className="bg-white p-6 rounded-lg shadow-md">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-gray-500">Total Users</p>
              <h3 className="text-2xl font-bold">{users.length}</h3>
            </div>
            <Users className="text-green-500" size={24} />
          </div>
        </div>

        <div className="bg-white p-6 rounded-lg shadow-md">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-gray-500">Products</p>
              <h3 className="text-2xl font-bold">--</h3>
            </div>
            <Package className="text-purple-500" size={24} />
          </div>
        </div>

        <div className="bg-white p-6 rounded-lg shadow-md">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-gray-500">Revenue</p>
              <h3 className="text-2xl font-bold">--</h3>
            </div>
            <TrendingUp className="text-red-500" size={24} />
          </div>
        </div>
      </div>

      <div className="bg-white rounded-lg shadow-md p-6">
        <div className="flex justify-between items-center mb-6">
          <h2 className="text-xl font-semibold">Staff Management</h2>
          <CrudButton
            label="Add Staff"
            icon={FilePlus}
            onClick={openAddModal}
            actionType="add"
          />
        </div>

        <div className="overflow-x-auto">
          <table className="min-w-full">
            <thead>
              <tr className="bg-gray-50">
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Name</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Email</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Role</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Actions</th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {users.map((user) => (
                <tr key={user.user_id}>
                  <td className="px-6 py-4 whitespace-nowrap">{user.name}</td>
                  <td className="px-6 py-4 whitespace-nowrap">{user.email}</td>
                  <td className="px-6 py-4 whitespace-nowrap">{user.role}</td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="flex space-x-2">
                      <CrudButton
                        label="Edit"
                        icon={Edit}
                        onClick={() => openEditModal(user)}
                        actionType="edit"
                      />
                      <CrudButton
                        label="Delete"
                        icon={Trash2}
                        onClick={() => handleDeleteUser(user.user_id)}
                        actionType="delete"
                      />
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {/* Modals */}
      {isModalOpen && (
        <UserModal
          isOpen={isModalOpen}
          onClose={closeModal}
          onSubmit={modalMode === 'add' ? handleAddUser : handleEditUser}
          mode={modalMode}
          userData={selectedUser}
        />
      )}

      <SuccessModal
        isOpen={successModalOpen}
        onClose={() => setSuccessModalOpen(false)}
        message={modalMessage}
      />

      <AlertModal
        isOpen={errorModalOpen}
        onClose={() => setErrorModalOpen(false)}
        message={modalMessage}
      />
    </div>
  );
};

export default DashboardAdmin;
