import clsx from "clsx";
import { Moon, Sun, Menu, Blocks } from 'lucide-react';

const Headers = ({ darkMode, toggleDarkMode }) => {  // Pass toggleDarkMode as a prop

    return (
        <nav className={clsx("fixed top-0 z-50 w-full bg-white",
            "border-b border-gray-200 dark:bg-gray-800 dark:border-gray-700",
            "px-4 py-3 lg:px-6 lg:py-4")}>  {/* Adjust padding for better spacing */}

            <div className="container mx-auto flex items-center justify-between">
                <button className={clsx("inline-flex items-center p-2 text-sm text-gray-500 rounded-lg",
                    "sm:hidden", "hover:bg-gray-100 focus:ring-gray-200",
                    "dark:text-gray-400 dark:hover:bg-gray-700 dark:focus:ring-gray-600")}>
                    <Menu className="text-2xl" />
                </button>
                <a href="/" className="flex items-center space-x-2">
                    <Blocks className="h-8 text-xl" style={{ color: "7C00FE" }} />
                    <span className="text-xl ml-2 font-bold">AStock</span>  {/* Add font-bold */}
                </a>
                <button
                    className="dark:bg-slate-50 dark:text-slate-700 rounded-full p-2"
                    onClick={toggleDarkMode}  // Add onClick handler
                >
                    {darkMode ? <Sun /> : <Moon />}
                </button>
            </div>

        </nav>
    );
};

export default Headers;
