import { Trash2 } from "lucide-react";
import DeleteModal from "../modal/DeleteModal";
import { useState } from "react";

const Trash = ({ onClick, deleteMessage, itemName, userName }) => {
  const [open, setOpen] = useState(false);

  return (
    <div>
      <button className="btn btn-danger" onClick={() => setOpen(true)}>
        <Trash2 /> Delete
      </button>
      <DeleteModal open={open} onClose={() => setOpen(false)}>
        <div className="text-center w-56">
          <Trash2 size={56} className="mx-auto text-red-600" />
          <div className="mx-auto my-4 w-48">
            <h3 className="text-lg font-black text-gray-800">Confirm Delete</h3>
            <p className="text-sm text-gray-500">
              {deleteMessage} <strong>{itemName}</strong> milik <strong>{userName}</strong>?
            </p>
          </div>
          <div className="flex gap-4">
            <button className="btn btn-danger w-full">Delete</button>
            <div className="button btn btn-light w-full" onClick={() => setOpen(false)}>Cancel</div>
          </div>
        </div>
      </DeleteModal>
    </div>
  );
};

export default Trash;
