import React, { useState } from 'react';
import Logo2 from "../../../assets/Logo2.png";

const Login = () => {
  const [isForgotPassword, setIsForgotPassword] = useState(false);

  const toggleForgotPassword = () => {
    setIsForgotPassword(!isForgotPassword);
  };

  return (
    <div className="flex justify-center items-center h-screen bg-gray-100 dark:bg-gray-800">
      <div className="relative bg-white dark:bg-gray-100 rounded-[30px] shadow-lg flex w-[900px] h-[500px] overflow-hidden transition-all duration-500 ease-in-out">
        
        {/* Left Side - Sign In */}
        <div className={`absolute w-1/2 h-full p-10 bg-white dark:bg-gray-100 flex flex-col justify-center items-center transform transition-transform duration-500 ease-in-out ${isForgotPassword ? '-translate-x-full' : 'translate-x-0'}`}>
          <img src={Logo2} alt="Logo" className="h-28 w-32 mb-6" />
          <h2 className="text-3xl font-bold mb-6">Login</h2>
          
          <form className="space-y-4">
            <input 
              type="email" 
              placeholder="Enter your Email" 
              className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500" 
            />
            <input 
              type="password" 
              placeholder="Password" 
              className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500" 
            />
            <button type="submit" className="w-full py-3 bg-purple-600 text-white rounded-lg hover:bg-purple-700 transition duration-300">
              Sign In
            </button>
          </form>
          <button className="mt-4 text-purple-600 hover:underline" onClick={toggleForgotPassword}>
            Forget Password?
          </button>
        </div>

        {/* Right Side - Forgot Password */}
        <div className={`absolute w-1/2 h-full p-10 bg-gradient-to-r from-purple-600 to-purple-400 dark:bg-gradient-to-br dark:from-purple-800 dark:to-blue-900 text-white flex flex-col justify-center items-center rounded-[150px_0_0_100px] transform transition-transform duration-500 ease-in-out ${isForgotPassword ? 'translate-x-0' : 'translate-x-full'}`}>
          <h2 className="text-3xl font-bold mb-4">Forget Password?</h2>
          <p className="mb-6 text-center">Enter your email to reset your password.</p>
          <input 
            type="email" 
            placeholder="Enter your Email" 
            className="w-full p-3 border border-gray-300 rounded-lg mb-4 focus:outline-none focus:ring-2 focus:ring-purple-500 text-gray-800" 
          />
          <button className="py-3 px-8 bg-white text-purple-600 rounded-lg hover:bg-gray-100 transition duration-300" onClick={toggleForgotPassword}>
            Reset
          </button>
        </div>
      </div>
    </div>
  );
};

export default Login;
