import React, { useEffect, useRef, useState } from 'react';
import axios from 'axios';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  TimeScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  LineController
} from 'chart.js';
import 'chartjs-adapter-date-fns'; 

ChartJS.register(
  CategoryScale,
  LinearScale,
  TimeScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  LineController
);

const OrderChart = () => {
  const [orders, setOrders] = useState([]);
  const chartRef = useRef(null);
  const chartInstance = useRef(null);

  useEffect(() => {
    fetchOrderData();
    return () => {
      // Pastikan chart dihancurkan saat komponen di-unmount
      if (chartInstance.current) {
        chartInstance.current.destroy();
      }
    };
  }, []);

  const fetchOrderData = async () => {
    try {
      const response = await axios.get('http://localhost:5000/orders');
      const orders = response.data;
      setOrders(orders);

      // Group orders by date
      const groupedOrders = orders.reduce((acc, order) => {
        const date = new Date(order.date).toLocaleDateString('en-US');
        if (!acc[date]) {
          acc[date] = [];
        }
        acc[date].push(order);
        return acc;
      }, {});

      const dates = Object.keys(groupedOrders);
      const orderCounts = dates.map(date => groupedOrders[date].length);

      // Destroy previous chart instance to avoid duplication
      if (chartInstance.current) {
        chartInstance.current.destroy();
      }

      // Create the chart
      chartInstance.current = new ChartJS(chartRef.current, {
        type: 'line',
        data: {
          labels: dates,
          datasets: [
            {
              label: 'Jumlah Order per Tanggal',
              data: orderCounts,
              borderColor: 'rgba(75, 192, 192, 1)',
              backgroundColor: 'rgba(75, 192, 192, 0.2)',
              fill: true,
            },
          ],
        },
        options: {
          responsive: true,
          scales: {
            x: {
              type: 'time',
              time: {
                unit: 'day', // Set unit waktu ke hari
                tooltipFormat: 'll',
              },
              title: {
                display: true,
                text: 'Tanggal',
              },
            },
            y: {
              title: {
                display: true,
                text: 'Jumlah Order',
              },
            },
          },
          plugins: {
            legend: {
              display: true,
            },
            tooltip: {
              callbacks: {
                label: function(context) {
                  let label = context.dataset.label || '';
                  if (label) {
                    label += ': ';
                  }
                  if (context.parsed.y !== null) {
                    label += new Intl.NumberFormat().format(context.parsed.y);
                  }
                  return label;
                }
              }
            }
          },
        },
      });
    } catch (error) {
      console.error('Error fetching order data:', error);
    }
  };

  return (
    <div className="container mx-auto p-5 mt-5">
      <h2 className="text-2xl font-bold mb-4">Order Chart</h2>
      <div>
        <canvas ref={chartRef} />
      </div>
    </div>
  );
};

export default OrderChart;
