import DeleteModal from "../modal/DeleteModal";
import { useState } from "react";

const CrudButton = ({
  icon: Icon,
  label,
  onConfirm,
  onClick,
  confirmMessage = "Apakah Anda yakin ingin melakukan aksi ini?",
  title = "Confirm Action",
  actionType = "default",
  className = "",
  buttonStyle = "primary"
}) => {
  const [open, setOpen] = useState(false);

  // Fungsi untuk menangani aksi delete yang memerlukan konfirmasi
  const handleConfirm = () => {
    if (onConfirm) onConfirm();
    setOpen(false);
  };

  // Handle tombol saat diklik
  const handleClick = () => {
    if (actionType === "delete") {
      setOpen(true); // Buka modal konfirmasi jika actionType adalah "delete"
    } else {
      onClick(); // Jalankan fungsi onClick langsung untuk edit atau add
    }
  };

  // Kelas dinamis berdasarkan buttonStyle
  const buttonClasses = `
    ${buttonStyle === "btn-primary" ? "bg-blue-500 text-white" : ""}
    ${buttonStyle === "btn-secondary" ? "bg-green-500 text-white" : ""}
    ${buttonStyle === "btn-danger" ? "bg-red-600 text-white" : ""}
    ${className}
  `;

  return (
    <div>
      <button className={`${buttonClasses}`} onClick={handleClick}>
        {Icon && <Icon className="mr-2" />} {label}
      </button>

      {actionType === "delete" && open && (
        <DeleteModal open={open} onClose={() => setOpen(false)}>
          <div className="text-center w-56">
            <Icon size={56} className="mx-auto text-red-600" />
            <div className="mx-auto my-4 w-48">
              <h3 className="text-lg font-black text-gray-800">{title}</h3>
              <p className="text-sm text-gray-500">{confirmMessage}</p>
            </div>
            <div className="flex gap-4">
              <button className="btn bg-red-600 text-white w-full" onClick={handleConfirm}>
                Delete
              </button>
              <button className="btn bg-gray-300 text-gray-800 w-full" onClick={() => setOpen(false)}>
                Cancel
              </button>
            </div>
          </div>
        </DeleteModal>
      )}
    </div>
  );
};

export default CrudButton;
