// OrderStock.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import AddOrder from './AddOrder';
import OrderList from './OrderList';

const OrderStock = () => {
  const [orders, setOrders] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [orderToEdit, setOrderToEdit] = useState(null);

  useEffect(() => {
    fetchOrders();
  }, []);

  const fetchOrders = async () => {
    try {
      const response = await axios.get('http://localhost:5000/orders');
      setOrders(response.data);
    } catch (error) {
      console.error('Error fetching orders:', error);
    }
  };

  const handleAddOrder = () => {
    fetchOrders();
    setOrderToEdit(null);
  };

  const handleEditOrder = (id) => {
    const order = orders.find(order => order.id === id);
    setOrderToEdit(order);
  };

  const handleDeleteOrder = async (id) => {
    if (window.confirm('Apakah Anda yakin ingin menghapus data ini?')) {
      try {
        await axios.delete(`http://localhost:5000/orders/${id}`);
        setOrders(orders.filter(order => order.id !== id));
      } catch (error) {
        console.error('Error deleting order:', error);
      }
    }
  };

  const filteredOrders = orders.filter((order) => {
    return (
      order.kodeBarang.toLowerCase().includes(searchTerm.toLowerCase()) ||
      order.namaBarang.toLowerCase().includes(searchTerm.toLowerCase())
    );
  });

  return (
    <div className="container mx-auto p-5 mt-20">
      <h1 className="text-3xl font-bold mb-4">Order Stok</h1>

      <input
        type="text"
        className="border p-2 mb-4 w-full"
        placeholder="Cari berdasarkan Kode Barang atau Nama Barang"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />

      <AddOrder orderToEdit={orderToEdit} onSave={handleAddOrder} onCancel={() => setOrderToEdit(null)} />

      <OrderList orders={filteredOrders} onEdit={handleEditOrder} onDelete={handleDeleteOrder} />
    </div>
  );
};

export default OrderStock;
