import React, { useState } from "react";
import { Outlet } from "react-router-dom";
import SidebarAdmin from "../Sidebar/admin/SidebarAdmin";
import Sidebars from "../Sidebar/staff/Sidebars";
import Headers from "../Sidebar/Headers";

const DashboardLayout = ({ toggleDarkMode, darkMode }) => {
    const [isSidebarOpen, setIsSidebarOpen] = useState(false);
    const role = localStorage.getItem("role");
    const SidebarComponent = role === "admin" ? SidebarAdmin : Sidebars;

    // Toggle sidebar visibility
    const toggleSidebar = () => {
        setIsSidebarOpen((prev) => !prev);
    };

    return (
        <div className={`flex min-h-screen ${darkMode ? "dark" : ""}`}>
            {/* Sidebar - Always visible on desktop */}
            <div className="hidden lg:flex">
            <SidebarComponent
                        isSidebarOpen={true} // Always open in desktop mode
                        darkMode={darkMode}
                        toggleDarkMode={toggleDarkMode}
                    />
            </div>

            {/* Mobile Sidebar */}
            <div
                className={`fixed z-40 inset-y-0 left-0 transform ${
                    isSidebarOpen ? "translate-x-0" : "-translate-x-full"
                } transition-transform duration-300 ease-in-out lg:hidden`}
            >
                <SidebarComponent
                    darkMode={darkMode}
                    toggleDarkMode={toggleDarkMode}
                    isSidebarOpen={isSidebarOpen}
                />
            </div>

            {/* Main Content Area */}
            <div className="flex-1 flex flex-col">
                {/* Desktop Header */}
                <div className="hidden lg:block">
                    <Headers toggleDarkMode={toggleDarkMode} darkMode={darkMode} toggleSidebar={toggleSidebar} />
                </div>

                {/* Mobile Header */}
                <div className="lg:hidden">
                    <Headers toggleDarkMode={toggleDarkMode} darkMode={darkMode} toggleSidebar={toggleSidebar} />
                </div>

                {/* Main Content */}
                <main className="flex-1 p-4 bg-gray-100">
                    <Outlet />
                </main>
            </div>
        </div>
    );
};

export default DashboardLayout;
