import React, { useState } from "react";
import { Outlet } from "react-router-dom";
import SidebarAdmin from "../Sidebar/admin/SidebarAdmin";
import Sidebars from "../Sidebar/staff/Sidebars";
import Headers from "../Sidebar/Headers";
import clsx from "clsx";
const DashboardLayout = () => {
  const [darkMode, setDarkMode] = useState(false);
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const role = localStorage.getItem("role");
  const SidebarComponent = role === "admin" ? SidebarAdmin : Sidebars;

  // Toggle sidebar visibility
  const toggleSidebar = () => {
    setIsSidebarOpen((prev) => !prev);
  };
  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
  };

  return (
    <div className="flex min-h-screen ">
      {/* Sidebar - hidden on mobile, visible on desktop */}
      <div className="hidden lg:flex">
        <SidebarComponent
          isSidebarOpen={true} // Always open in desktop mode
          darkMode={darkMode}
          toggleDarkMode={toggleDarkMode}
        />
      </div>

      {/* Mobile Sidebar - controlled by the toggle button */}
      <div
        className={`fixed z-40 inset-y-0 left-0 transform ${
          isSidebarOpen ? "translate-x-0" : "-translate-x-full"
        } transition-transform duration-300 ease-in-out lg:hidden`}
      >
        <SidebarComponent
          isSidebarOpen={isSidebarOpen} // Open/Close in mobile mode
          darkMode={darkMode}
          toggleDarkMode={toggleDarkMode}
        />
      </div>



      {/* Main content area */}
      <div
        className={clsx(
          "flex-1 flex flex-col transition-all duration-300 ease-in-out",
          isSidebarOpen ? "lg:ml-64" : "lg:ml-0" // Geser main content jika sidebar terbuka
        )}
      >
        

        {/* Main Content */}
        <main className="flex-1 p-4 lg:ml-64 bg-gray-100">
          <Outlet />
        </main>
      </div>
    </div>
  );
};

export default DashboardLayout;
