import React, { useState } from "react";
import clsx from "clsx";
import { Outlet } from "react-router-dom";
import SidebarAdmin from "../Sidebar/admin/SidebarAdmin"; // Admin Sidebar
import Sidebars from "../Sidebar/staff/Sidebars"; // Staff Sidebar
import Headers from "../Sidebar/Headers"; // Header Component

const DashboardLayout = () => {
  const [darkMode, setDarkMode] = useState(false);
  const [isSidebarMobileOpen, setIsSidebarMobileOpen] = useState(false); // Mobile sidebar
  const [isSidebarDesktopOpen, setIsSidebarDesktopOpen] = useState(true); // Desktop sidebar

  const role = localStorage.getItem("role"); // Ambil role dari localStorage
  const SidebarComponent = role === "admin" ? SidebarAdmin : Sidebars;

  // Fungsi toggle untuk sidebar mobile
  const toggleMobileSidebar = () => {
    setIsSidebarMobileOpen((prev) => !prev);
  };

  // Fungsi toggle untuk sidebar desktop
  const toggleDesktopSidebar = () => {
    setIsSidebarDesktopOpen((prev) => !prev);
  };

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
  };

  return (
    <div className="flex min-h-screen bg-gray-100">
      {/* Sidebar */}
      <div
        className={clsx(
          "fixed z-40 inset-y-0 left-0 bg-white dark:bg-gray-800 shadow-lg transition-transform duration-300 ease-in-out",
          isSidebarMobileOpen ? "translate-x-0 w-64" : "-translate-x-full", // Mobile
          isSidebarDesktopOpen ? "lg:translate-x-0 lg:w-64" : "lg:-translate-x-full" // Desktop
        )}
      >
        <SidebarComponent
          isSidebarOpen={isSidebarMobileOpen || isSidebarDesktopOpen}
          darkMode={darkMode}
          toggleDarkMode={toggleDarkMode}
        />
      </div>

      {/* Header */}
      <div className={`${darkMode && "dark"}`}>
        <Headers
          toggleDarkMode={toggleDarkMode}
          darkMode={darkMode}
          toggleMobileSidebar={toggleMobileSidebar} // Mobile
          toggleDesktopSidebar={toggleDesktopSidebar} // Desktop
        />
      </div>

      {/* Main Content Area */}
      <div
        className={clsx(
          "flex-1 flex flex-col transition-all duration-300 ease-in-out overflow-x-hidden",
          isSidebarDesktopOpen ? "lg:ml-64" : "lg:ml-0" // Geser main content jika sidebar terbuka (desktop)
        )}
      >
        {/* Main Content */}
        <main className="flex-1 p-4">
          <Outlet />
        </main>
      </div>
    </div>
  );
};

export default DashboardLayout;
