import { useState } from 'react';
import clsx from "clsx";
import { Moon, Sun, Menu } from 'lucide-react';
import Logo2 from "../../../assets/Logo2.png";

const Headers = ({ darkMode, toggleDarkMode, toggleSidebar }) => {
    return (
        <nav className={clsx("fixed top-0 z-50 w-full bg-white",
            "border-b border-gray-200 dark:bg-gray-800 dark:border-gray-700")}>
            <div className="px-2 py-3 lg:px-5 lg:pl-3">
                <div className="flex items-center justify-between">
                    <div className="flex items-center justify-start rtl:justify-end">
                        {/* Tombol menu hanya muncul di layar kecil (mobile) */}
                        <button className={clsx("inline-flex items-center p-2 text-sm text-gray-500 rounded-lg",
                            "sm:hidden", // Disembunyikan di layar lebih besar dari 640px
                            "hover:bg-gray-100 focus:ring-gray-200",
                            "dark:text-gray-400 dark:hover:bg-gray-700 dark:focus:ring-gray-600"
                        )}
                            onClick={toggleSidebar}
                        >
                            <Menu className="text-2xl" />
                        </button>
                        <a href="/#" className="flex items-center space-x-2">
                            <img src={Logo2} alt="Logo" width="60" height="50" />
                            <span className="self-center text-xl font-semibold sm:text-2xl whitespace-nowrap dark:text-white">SIOPS</span>
                        </a>
                    </div>
                    <button className="dark:bg-slate-50 dark:text-slate-700 rounded-full p-2"
                        onClick={toggleDarkMode}>
                        {darkMode ? <Sun /> : <Moon />}
                    </button>
                </div>
            </div>
        </nav>
    );
};

const Sidebars = ({ isSidebarOpen }) => {
    return (
        <aside
            className={clsx(
                "fixed top-0 left-0 z-40 w-64 h-screen pt-20",
                "bg-white border-r border-gray-200",
                "dark:bg-gray-800 dark:border-gray-700",
                "transition-transform mt-2 dark:text-white",
                // Sidebar hanya tampil di layar besar (sm: di atas 640px) dan muncul jika dibuka di layar kecil
                {
                    "translate-x-0 sm:translate-x-0": isSidebarOpen, // Sidebar terbuka di layar kecil saat tombol menu diklik
                    "-translate-x-full sm:translate-x-0": !isSidebarOpen // Sembunyikan sidebar di layar kecil saat tidak dibuka
                }
            )}
        >
            <div className="h-full px-3 pb-4 overflow-y-auto">
                <ul className="space-y-2 font-medium">
                    {/* Isi menu sidebar */}
                </ul>
            </div>
        </aside>
    );
};

export default function App() {
    const [darkMode, setDarkMode] = useState(false);
    const [isSidebarOpen, setSidebarOpen] = useState(false);

    const toggleDarkMode = () => setDarkMode(!darkMode);
    const toggleSidebar = () => setSidebarOpen(!isSidebarOpen);

    return (
        <div className={clsx(darkMode ? 'dark' : '')}>
            {/* Header */}
            <Headers darkMode={darkMode} toggleDarkMode={toggleDarkMode} toggleSidebar={toggleSidebar} />

            {/* Sidebar */}
            <Sidebars isSidebarOpen={isSidebarOpen} />

            {/* Konten utama */}
            <main className="pt-16">
                <div className="p-4">
                    {/* Konten Anda di sini */}
                </div>
            </main>
        </div>
    );
}
