import React, { useState, useEffect } from "react";
import { Outlet } from "react-router-dom";
import SidebarAdmin from "../Sidebar/admin/SidebarAdmin";
import Sidebars from "../Sidebar/staff/Sidebars";
import Headers from "../Sidebar/Headers";

const DashboardLayout = () => {
    const [isSidebarOpen, setIsSidebarOpen] = useState(false);
    const [darkMode, setDarkMode] = useState(false); // State untuk dark mode
    const role = localStorage.getItem("role");
    const SidebarComponent = role === "admin" ? SidebarAdmin : Sidebars;

    // Toggle sidebar visibility
    const toggleSidebar = () => {
        setIsSidebarOpen((prev) => !prev);
    };

    // Toggle dark mode
    const toggleDarkMode = () => {
        setDarkMode((prev) => !prev);
    };

    // Apply dark mode class to <html>
    useEffect(() => {
        if (darkMode) {
            document.documentElement.classList.add("dark");
        } else {
            document.documentElement.classList.remove("dark");
        }
    }, [darkMode]);

    return (
        <div className={`flex min-h-screen ${darkMode ? "dark bg-gray-900" : "bg-gray-100"}`}>
            {/* Sidebar - hidden on mobile, visible on desktop */}
            <div className="hidden lg:flex">
                <SidebarComponent
                    isSidebarOpen={true} // Always open in desktop mode
                    darkMode={darkMode}
                    toggleDarkMode={toggleDarkMode}
                />
            </div>

            {/* Mobile Sidebar - controlled by the toggle button */}
            <div
                className={`fixed z-40 inset-y-0 left-0 transform ${
                    isSidebarOpen ? "translate-x-0" : "-translate-x-full"
                } transition-transform duration-300 ease-in-out lg:hidden`}
            >
                <SidebarComponent
                    isSidebarOpen={isSidebarOpen} // Open/Close in mobile mode
                    darkMode={darkMode}
                    toggleDarkMode={toggleDarkMode}
                />
            </div>

            {/* Main content area */}
            <div className="flex-1 flex flex-col">
                {/* Mobile Header with Hamburger Menu */}
                <div className="lg:hidden">
                    <Headers
                        toggleDarkMode={toggleDarkMode}
                        darkMode={darkMode}
                        toggleSidebar={toggleSidebar}
                    />
                </div>

                {/* Main Content */}
                <main className="flex-1 p-4 lg:ml-64 dark:bg-gray-900 dark:text-white bg-gray-100">
                    <Outlet />
                </main>
            </div>
        </div>
    );
};

export default DashboardLayout;
