import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import CosForm from '../../CardForm/CosForm'; // Ensure the path is correct

const AddUser = () => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState ("");
  const navigate = useNavigate();

  const saveUser = async (userData) => {
    userData.preventDefault();
    try {
      const token = localStorage.getItem('token');
      await axios.post('http://localhost:5000/users', userData, {
        headers: {
          Authorization: `Bearer ${token}`,
        },  
        name,
        email,
        password,
        role
      });
      navigate("/users");
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <CosForm title="Add User" onSubmit={saveUser}>
      <div className="mb-4">
        <label className="block text-white text-sm font-bold mb-2" htmlFor="name">
          Name
        </label>
        <input
          className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
          id="name"
          value={name}
          onChange={(userData) => setName(userData.target.value)}
          type="text"
          placeholder="Name"
        />
      </div>
      <div className="mb-4">
        <label className="block text-white text-sm font-bold mb-2" htmlFor="email">
          Email
        </label>
        <input
          className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
          id="email"
          value={email}
          onChange={(userData) => setEmail(userData.target.value)}
          type="email"
          placeholder="Email"
        />
      </div>
      <div className="mb-4">
        <label className="block text-white text-sm font-bold mb-2" htmlFor="password">
          Password
        </label>
        <input
          className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
          id="password"
          value={password}
          onChange={(userData) => setPassword(userData.target.value)}
          type="password"
          placeholder="Password"
        />
      </div>
      <div className="mb-4">
        <label className="block text-white text-sm font-bold mb-2" htmlFor="password">
          Password
        </label>
        <input
          className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
          id="password"
          value={password}
          onChange={(userData) => setPassword(userData.target.value)}
          type="password"
          placeholder="Password"
        />
      </div>
      <div className="flex items-center justify-between">
        <button
          className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
          type="submit"
        >
          Save
        </button>
      </div>
    </CosForm>
  );
};

export default AddUser;
